  // List support tickets for clinic
  app.get('/api/support/tickets', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;

      // Get user's clinic
      const [clinicMember] = await db.select()
        .from(clinicMembers)
        .innerJoin(clinics, eq(clinicMembers.clinicId, clinics.id))
        .where(eq(clinicMembers.userId, userId));

      if (!clinicMember) {
        return res.status(403).json({ message: "No clinic access" });
      }

      const { status, priority, category, limit = 50, offset = 0 } = req.query;
      const limitNum = Math.min(parseInt(limit as string) || 50, 100);
      const offsetNum = parseInt(offset as string) || 0;

      let whereConditions = [eq(supportTickets.clinicId, clinicMember.clinicMembers.clinicId)];

      if (status) {
        whereConditions.push(eq(supportTickets.status, status as string));
      }
      if (priority) {
        whereConditions.push(eq(supportTickets.priority, priority as string));
      }
      if (category) {
        whereConditions.push(eq(supportTickets.category, category as string));
      }

      const tickets = await db
        .select({
          id: supportTickets.id,
          title: supportTickets.title,
          description: supportTickets.description,
          priority: supportTickets.priority,
          category: supportTickets.category,
          status: supportTickets.status,
          tags: supportTickets.tags,
          firstResponseAt: supportTickets.firstResponseAt,
          resolvedAt: supportTickets.resolvedAt,
          createdAt: supportTickets.createdAt,
          updatedAt: supportTickets.updatedAt,
        })
        .from(supportTickets)
        .where(and(...whereConditions))
        .orderBy(desc(supportTickets.createdAt))
        .limit(limitNum)
        .offset(offsetNum);

      const totalCount = await db
        .select({ count: sql<number>`count(*)` })
        .from(supportTickets)
        .where(and(...whereConditions));

      res.json({
        tickets,
        pagination: {
          limit: limitNum,
          offset: offsetNum,
          total: totalCount[0]?.count || 0,
          hasMore: (offsetNum + limitNum) < (totalCount[0]?.count || 0),
        },
      });
    } catch (error) {
      console.error("Error fetching support tickets:", error);
      res.status(500).json({ message: "Failed to fetch support tickets" });
    }
  });

  // Add comment to support ticket
  app.post('/api/support/tickets/:ticketId/comments', isAuthenticated, auditCreate('ticket_comment'), async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { ticketId } = req.params;
      const { content } = req.body;

      // Verify ticket access
      const [ticket] = await db.select()
        .from(supportTickets)
        .innerJoin(clinicMembers, eq(supportTickets.clinicId, clinicMembers.clinicId))
        .where(
          and(
            eq(supportTickets.id, ticketId),
            eq(clinicMembers.userId, userId)
          )
        );

      if (!ticket) {
        return res.status(404).json({ message: "Ticket not found or no access" });
      }

      const [comment] = await db.insert(ticketComments).values({
        ticketId,
        userId,
        content,
        isInternal: false,
      }).returning();

      // Update first response timestamp if this is first response
      if (!ticket.support_tickets.firstResponseAt) {
        await db
          .update(supportTickets)
          .set({
            firstResponseAt: new Date(),
            status: 'in_progress',
            updatedAt: new Date(),
          })
          .where(eq(supportTickets.id, ticketId));
      } else {
        await db
          .update(supportTickets)
          .set({ updatedAt: new Date() })
          .where(eq(supportTickets.id, ticketId));
      }

      res.status(201).json({
        message: "Comment added successfully",
        comment
      });
    } catch (error) {
      console.error("Error adding ticket comment:", error);
      res.status(500).json({ message: "Failed to add comment" });
    }
  });

  // Get onboarding checklist for clinic
  app.get('/api/onboarding/checklist', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;

      // Get user's clinic
      const [clinicMember] = await db.select()
        .from(clinicMembers)
        .innerJoin(clinics, eq(clinicMembers.clinicId, clinics.id))
        .where(eq(clinicMembers.userId, userId));

      if (!clinicMember) {
        return res.status(403).json({ message: "No clinic access" });
      }

      let [checklist] = await db
        .select()
        .from(onboardingChecklists)
        .where(eq(onboardingChecklists.clinicId, clinicMember.clinicMembers.clinicId));

      // Create default checklist if none exists
      if (!checklist) {
        const standardProgress = {
          step1: { completed: false, name: "Complete clinic profile", description: "Add clinic details and contact information" },
          step2: { completed: false, name: "Configure AI settings", description: "Set up your AI receptionist personality and responses" },
          step3: { completed: false, name: "Test voice calls", description: "Make test calls to verify your setup" },
          step4: { completed: false, name: "Set up integrations", description: "Connect your calendar and other systems" },
          step5: { completed: false, name: "Train your team", description: "Brief your team on the new AI system" },
          step6: { completed: false, name: "Go live", description: "Start accepting real calls through AI" },
        };

        [checklist] = await db.insert(onboardingChecklists).values({
          clinicId: clinicMember.clinicMembers.clinicId,
          templateName: 'standard',
          progress: standardProgress,
          totalSteps: 6,
        }).returning();
      }

      res.json({ checklist });
    } catch (error) {
      console.error("Error fetching onboarding checklist:", error);
      res.status(500).json({ message: "Failed to fetch onboarding checklist" });
    }
  });

  // Update onboarding progress
  app.post('/api/onboarding/progress', isAuthenticated, auditUpdate('onboarding'), async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { stepKey, completed } = req.body;

      // Get user's clinic
      const [clinicMember] = await db.select()
        .from(clinicMembers)
        .innerJoin(clinics, eq(clinicMembers.clinicId, clinics.id))
        .where(eq(clinicMembers.userId, userId));

      if (!clinicMember) {
        return res.status(403).json({ message: "No clinic access" });
      }

      const [checklist] = await db
        .select()
        .from(onboardingChecklists)
        .where(eq(onboardingChecklists.clinicId, clinicMember.clinicMembers.clinicId));

      if (!checklist) {
        return res.status(404).json({ message: "Onboarding checklist not found" });
      }

      // Update progress
      const updatedProgress = { ...checklist.progress };
      if (updatedProgress[stepKey]) {
        updatedProgress[stepKey].completed = completed;
        if (completed) {
          updatedProgress[stepKey].completedAt = new Date().toISOString();
        }
      }

      // Calculate current step and completion
      const completedSteps = Object.values(updatedProgress).filter((step: any) => step.completed).length;
      const isFullyCompleted = completedSteps === checklist.totalSteps;

      await db
        .update(onboardingChecklists)
        .set({
          progress: updatedProgress,
          currentStep: completedSteps,
          completedAt: isFullyCompleted ? new Date() : null,
          updatedAt: new Date(),
        })
        .where(eq(onboardingChecklists.id, checklist.id));

      res.json({
        message: "Onboarding progress updated successfully",
        completedSteps,
        totalSteps: checklist.totalSteps,
        isCompleted: isFullyCompleted,
      });
    } catch (error) {
      console.error("Error updating onboarding progress:", error);
      res.status(500).json({ message: "Failed to update onboarding progress" });
    }
  });

  // Get customer health score
  app.get('/api/customer-success/health-score', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;

      // Get user's clinic
      const [clinicMember] = await db.select()
        .from(clinicMembers)
        .innerJoin(clinics, eq(clinicMembers.clinicId, clinics.id))
        .where(eq(clinicMembers.userId, userId));

      if (!clinicMember) {
        return res.status(403).json({ message: "No clinic access" });
      }

      const currentPeriod = new Date().toISOString().slice(0, 7); // YYYY-MM format

      const [healthScore] = await db
        .select()
        .from(customerHealthScores)
        .where(
          and(
            eq(customerHealthScores.clinicId, clinicMember.clinicMembers.clinicId),
            eq(customerHealthScores.period, currentPeriod)
          )
        );

      // Calculate health score if not exists
      if (!healthScore) {
        // This would typically be calculated by a background job
        // For now, return a placeholder
        const calculatedScore = {
          clinicId: clinicMember.clinicMembers.clinicId,
          period: currentPeriod,
          overallScore: 75,
          usageScore: 80,
          engagementScore: 70,
          supportScore: 85,
          retentionRisk: 'low' as const,
          actionItems: ['Consider upgrading to Pro plan for advanced features'],
        };

        const [newScore] = await db
          .insert(customerHealthScores)
          .values(calculatedScore)
          .returning();

        return res.json({ healthScore: newScore });
      }

      res.json({ healthScore });
    } catch (error) {
      console.error("Error fetching customer health score:", error);
      res.status(500).json({ message: "Failed to fetch health score" });
    }
  });

  // Get system status for status page
  app.get('/api/status', async (req, res) => {
    try {
      const currentIncidents = await db
        .select()
        .from(systemIncidents)
        .where(ne(systemIncidents.status, 'resolved'))
        .orderBy(desc(systemIncidents.startedAt))
        .limit(10);

      const recentUpdates = await db
        .select({
          id: incidentUpdates.id,
          incidentId: incidentUpdates.incidentId,
          message: incidentUpdates.message,
          status: incidentUpdates.status,
          createdAt: incidentUpdates.createdAt,
          incidentTitle: systemIncidents.title,
        })
        .from(incidentUpdates)
        .innerJoin(systemIncidents, eq(incidentUpdates.incidentId, systemIncidents.id))
        .orderBy(desc(incidentUpdates.createdAt))
        .limit(20);

      // System status based on incidents
      const systemStatus = currentIncidents.length === 0 ? 'operational' :
                          currentIncidents.some(i => i.impact === 'critical') ? 'major_outage' :
                          currentIncidents.some(i => i.impact === 'major') ? 'partial_outage' :
                          'degraded_performance';

      res.json({
        status: systemStatus,
        incidents: currentIncidents,
        recentUpdates,
        services: {
          'API': { status: 'operational' },
          'Voice Calls': { status: 'operational' },
          'Dashboard': { status: 'operational' },
          'Integrations': { status: 'operational' },
        }
      });
    } catch (error) {
      console.error("Error fetching system status:", error);
      res.status(500).json({ message: "Failed to fetch system status" });
    }
  });

  // Operational Monitoring and Health Check Routes

  // Comprehensive health check endpoint
  app.get('/health', async (req, res) => {
    const healthChecks = {
      status: 'ok',
      timestamp: new Date().toISOString(),
      version: process.env.npm_package_version || '1.0.0',
      uptime: process.uptime(),
      environment: process.env.NODE_ENV || 'development',
      checks: {
        database: { status: 'ok', latency: 0 },
        memory: { status: 'ok', usage: 0, percentage: 0 },
        twilio: { status: 'unknown' },
        elevenlabs: { status: 'unknown' }
      }
    };

    try {
      // Database health check
      const dbStart = Date.now();
      await db.select({ count: sql`count(*)` }).from(clinics).limit(1);
      const dbLatency = Date.now() - dbStart;
      healthChecks.checks.database = { 
        status: dbLatency < 1000 ? 'ok' : 'slow', 
        latency: dbLatency 
      };

      // Memory usage check
      const memUsage = process.memoryUsage();
      const memPercentage = (memUsage.heapUsed / memUsage.heapTotal) * 100;
      healthChecks.checks.memory = {
        status: memPercentage < 80 ? 'ok' : 'warning',
        usage: Math.round(memUsage.heapUsed / 1024 / 1024), // MB
        percentage: Math.round(memPercentage)
      };

      // External service checks (simplified for now)
      healthChecks.checks.twilio.status = process.env.TWILIO_ACCOUNT_SID ? 'configured' : 'not_configured';
      healthChecks.checks.elevenlabs.status = process.env.ELEVENLABS_API_KEY ? 'configured' : 'not_configured';

      // Overall status determination
      const hasErrors = Object.values(healthChecks.checks).some(check => 
        typeof check === 'object' && check.status === 'error'
      );
      const hasWarnings = Object.values(healthChecks.checks).some(check => 
        typeof check === 'object' && (check.status === 'warning' || check.status === 'slow')
      );

      if (hasErrors) {
        healthChecks.status = 'error';
        res.status(503);
      } else if (hasWarnings) {
        healthChecks.status = 'warning';
        res.status(200);
      }

      res.json(healthChecks);
    } catch (error) {
      console.error('Health check failed:', error);
      healthChecks.status = 'error';
      healthChecks.checks.database.status = 'error';
      res.status(503).json(healthChecks);
    }
  });

  // Detailed metrics endpoint (admin only)
  app.get('/api/admin/metrics', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const [user] = await db.select().from(users).where(eq(users.id, userId));

      if (!user || user.role !== 'admin') {
        return res.status(403).json({ message: "Admin access required" });
      }

      // Get platform-wide metrics
      const [clinicStats] = await db
        .select({
          totalClinics: sql<number>`count(*)`,
          activeClinics: sql<number>`count(*) filter (where subscription_tier != 'free')`,
          totalCalls: sql<number>`sum(total_calls)`,
          monthlyCallsUsed: sql<number>`sum(monthly_calls_used)`
        })
        .from(clinics);

      const [userStats] = await db
        .select({
          totalUsers: sql<number>`count(*)`,
          adminUsers: sql<number>`count(*) filter (where role = 'admin')`,
          clinicOwners: sql<number>`count(*) filter (where role = 'clinic_owner')`
        })
        .from(users);

      // Get subscription distribution
      const subscriptionStats = await db
        .select({
          tier: clinics.subscriptionTier,
          count: sql<number>`count(*)`
        })
        .from(clinics)
        .groupBy(clinics.subscriptionTier);

      // Calculate financial metrics (simplified)
      const monthlyRevenue = subscriptionStats.reduce((total, tier) => {
        const pricing = {
          essential: 49,
          professional: 149,
          enterprise: 299
        };
        return total + (pricing[tier.tier as keyof typeof pricing] || 0) * tier.count;
      }, 0);

      const metrics = {
        timestamp: new Date().toISOString(),
        platform: {
          clinics: clinicStats,
          users: userStats,
          subscriptions: subscriptionStats,
          revenue: {
            monthlyRecurring: monthlyRevenue,
            currency: 'GBP'
          }
        },
        system: {
          uptime: process.uptime(),
          memory: process.memoryUsage(),
          version: process.env.npm_package_version || '1.0.0',
          environment: process.env.NODE_ENV || 'development'
        }
      };

      res.json(metrics);
    } catch (error) {
      console.error("Error fetching metrics:", error);
      res.status(500).json({ message: "Failed to fetch metrics" });
    }
  });

  // Error reporting endpoint for frontend
  app.post('/api/errors/report', isAuthenticated, async (req: any, res) => {
    try {
      const { error, context, userAgent, url } = req.body;
      const userId = req.user.claims.sub;

      // Log error for monitoring (in production, send to error tracking service)
      console.error('Frontend error reported:', {
        userId,
        error,
        context,
        userAgent,
        url,
        timestamp: new Date().toISOString()
      });

      // In production, you'd send this to Sentry, DataDog, etc.
      // For now, we'll just acknowledge receipt
      res.json({ 
        acknowledged: true, 
        errorId: `err_${Date.now()}_${Math.random().toString(36).substring(7)}` 
      });
    } catch (error) {
      console.error("Error logging frontend error:", error);
      res.status(500).json({ message: "Failed to log error" });
    }
  });

  // Usage Tracking and Quota Management Routes

  // Get current usage for a clinic
  app.get('/api/clinics/:clinicId/usage', isAuthenticated, async (req: any, res) => {
    try {
      const { clinicId } = req.params;
      const userId = req.user.claims.sub;

      // Verify access to clinic
      const [clinic] = await db.select().from(clinics).where(eq(clinics.id, clinicId));
      if (!clinic) {
        return res.status(404).json({ message: "Clinic not found" });
      }

      // Check if user has access (owner or member)
      if (clinic.ownerId !== userId) {
        const [member] = await db
          .select()
          .from(clinicMembers)
          .where(and(
            eq(clinicMembers.userId, userId),
            eq(clinicMembers.clinicId, clinicId),
            eq(clinicMembers.isActive, true)
          ));

        if (!member) {
          return res.status(403).json({ message: "Access denied to this clinic" });
        }
      }

      // Get current month usage
      const currentPeriod = new Date().toISOString().slice(0, 7); // YYYY-MM
      const [usage] = await db
        .select()
        .from(usageTracking)
        .where(and(
          eq(usageTracking.clinicId, clinicId),
          eq(usageTracking.period, currentPeriod)
        ));

      // Get subscription limits from pricing
      const limits = {
        essential: { calls: 1000, unlimited: false },
        professional: { calls: 2000, unlimited: false },
        enterprise: { calls: -1, unlimited: true }
      };

      const subscriptionLimits = limits[clinic.subscriptionTier as keyof typeof limits] || limits.essential;

      res.json({
        period: currentPeriod,
        usage: usage || {
          callsCount: 0,
          callMinutes: 0,
          aiCharactersUsed: 0,
          appointmentsBooked: 0
        },
        limits: subscriptionLimits,
        subscriptionTier: clinic.subscriptionTier
      });
    } catch (error) {
      console.error("Error fetching usage:", error);
      res.status(500).json({ message: "Failed to fetch usage data" });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import {
  users,
  clinics,
  callLogs,
  appointments,
  aiConfigurations,
  type User,
  type UpsertUser,
  type Clinic,
  type InsertClinic,
  type CallLog,
  type InsertCallLog,
  type Appointment,
  type InsertAppointment,
  type AiConfiguration,
  type InsertAiConfiguration,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Clinic operations
  getClinicByUserId(userId: string): Promise<Clinic | undefined>;
  createClinic(clinic: InsertClinic): Promise<Clinic>;
  updateClinic(id: string, clinic: Partial<InsertClinic>): Promise<Clinic>;

  // Call log operations
  getCallLogsByClinicId(clinicId: string): Promise<CallLog[]>;
  createCallLog(callLog: InsertCallLog): Promise<CallLog>;
  getCallLogById(id: string): Promise<CallLog | undefined>;

  // Appointment operations
  getAppointmentsByClinicId(clinicId: string): Promise<Appointment[]>;
  getTodayAppointmentsByClinicId(clinicId: string): Promise<Appointment[]>;
  createAppointment(appointment: InsertAppointment): Promise<Appointment>;
  updateAppointment(id: string, appointment: Partial<InsertAppointment>): Promise<Appointment>;

  // AI configuration operations
  getAiConfigurationByClinicId(clinicId: string): Promise<AiConfiguration | undefined>;
  createAiConfiguration(config: InsertAiConfiguration): Promise<AiConfiguration>;
  updateAiConfiguration(id: string, config: Partial<InsertAiConfiguration>): Promise<AiConfiguration>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Clinic operations
  async getClinicByUserId(userId: string): Promise<Clinic | undefined> {
    const [clinic] = await db.select().from(clinics).where(eq(clinics.ownerId, userId));
    return clinic;
  }

  async createClinic(clinic: InsertClinic): Promise<Clinic> {
    const [newClinic] = await db.insert(clinics).values(clinic).returning();
    return newClinic;
  }

  async updateClinic(id: string, clinic: Partial<InsertClinic>): Promise<Clinic> {
    const [updatedClinic] = await db
      .update(clinics)
      .set({ ...clinic, updatedAt: new Date() })
      .where(eq(clinics.id, id))
      .returning();
    return updatedClinic;
  }

  // Call log operations
  async getCallLogsByClinicId(clinicId: string): Promise<CallLog[]> {
    return await db
      .select()
      .from(callLogs)
      .where(eq(callLogs.clinicId, clinicId))
      .orderBy(desc(callLogs.createdAt));
  }

  async createCallLog(callLog: InsertCallLog): Promise<CallLog> {
    const [newCallLog] = await db.insert(callLogs).values(callLog).returning();
    return newCallLog;
  }

  async getCallLogById(id: string): Promise<CallLog | undefined> {
    const [callLog] = await db.select().from(callLogs).where(eq(callLogs.id, id));
    return callLog;
  }

  // Appointment operations
  async getAppointmentsByClinicId(clinicId: string): Promise<Appointment[]> {
    return await db
      .select()
      .from(appointments)
      .where(eq(appointments.clinicId, clinicId))
      .orderBy(desc(appointments.appointmentDate));
  }

  async getTodayAppointmentsByClinicId(clinicId: string): Promise<Appointment[]> {
    const today = new Date();
    const startOfDay = new Date(today.getFullYear(), today.getMonth(), today.getDate());
    const endOfDay = new Date(today.getFullYear(), today.getMonth(), today.getDate() + 1);

    return await db
      .select()
      .from(appointments)
      .where(
        and(
          eq(appointments.clinicId, clinicId),
          // Note: For simplicity, we'll fetch all appointments and filter on frontend
          // In production, you'd want proper date filtering
        )
      )
      .orderBy(appointments.appointmentDate);
  }

  async createAppointment(appointment: InsertAppointment): Promise<Appointment> {
    const [newAppointment] = await db.insert(appointments).values(appointment).returning();
    return newAppointment;
  }

  async updateAppointment(id: string, appointment: Partial<InsertAppointment>): Promise<Appointment> {
    const [updatedAppointment] = await db
      .update(appointments)
      .set({ ...appointment, updatedAt: new Date() })
      .where(eq(appointments.id, id))
      .returning();
    return updatedAppointment;
  }

  // AI configuration operations
  async getAiConfigurationByClinicId(clinicId: string): Promise<AiConfiguration | undefined> {
    const [config] = await db
      .select()
      .from(aiConfigurations)
      .where(eq(aiConfigurations.clinicId, clinicId));
    return config;
  }

  async createAiConfiguration(config: InsertAiConfiguration): Promise<AiConfiguration> {
    const [newConfig] = await db.insert(aiConfigurations).values(config).returning();
    return newConfig;
  }

  async updateAiConfiguration(id: string, config: Partial<InsertAiConfiguration>): Promise<AiConfiguration> {
    const [updatedConfig] = await db
      .update(aiConfigurations)
      .set({ ...config, updatedAt: new Date() })
      .where(eq(aiConfigurations.id, id))
      .returning();
    return updatedConfig;
  }
}

export const storage = new DatabaseStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from 'drizzle-orm';
import {
  pgTable,
  uuid,
  text,
  timestamp,
  integer,
  real,
  boolean,
  jsonb,
  varchar,
  index,
  decimal,
  serial,
} from "drizzle-orm/pg-core";
import { relations } from "drizzle-orm";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Session storage table (required for Replit Auth)
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// User storage table (required for Replit Auth)
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  role: varchar("role").notNull().default("clinic_owner"), // clinic_owner, admin
  isActive: boolean("is_active").notNull().default(true),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Clinics table
export const clinics = pgTable("clinics", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  ownerId: varchar("owner_id").notNull().references(() => users.id, { onDelete: "cascade" }),
  name: text("name").notNull(),
  phoneNumber: text("phone_number"),
  address: text("address"),
  email: varchar("email"),
  website: varchar("website"),
  description: text("description"),
  logo: text("logo"), // URL to logo image  
  subscriptionStatus: text("subscription_status").default("trial"),
  subscriptionTier: text("subscription_tier").default("basic"),
  billingCycle: varchar("billing_cycle").default("monthly"), // monthly, yearly
  subscriptionStartDate: timestamp("subscription_start_date"),
  subscriptionEndDate: timestamp("subscription_end_date"),
  trialEndsAt: timestamp("trial_ends_at"),
  totalCalls: integer("total_calls").notNull().default(0),
  totalAppointments: integer("total_appointments").notNull().default(0),
  monthlyCallsUsed: integer("monthly_calls_used").notNull().default(0),
  callsLimit: integer("calls_limit").notNull().default(100),
  currency: text("currency").default("GBP"),
  googleSheetsId: text("google_sheets_id"),
  isActive: boolean("is_active").notNull().default(true),
  // Tenant lifecycle management fields
  status: varchar("status").default("active"), // active, suspended, deleted
  suspensionReason: text("suspension_reason"),
  suspendedAt: timestamp("suspended_at"),
  suspendedBy: varchar("suspended_by"),
  reactivatedAt: timestamp("reactivated_at"),
  reactivatedBy: varchar("reactivated_by"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Call logs table
export const callLogs: any = pgTable("call_logs", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").references(() => clinics.id, { onDelete: "cascade" }),
  twilioCallSid: varchar("twilio_call_sid"),
  callerPhone: text("caller_phone").notNull(),
  toNumber: varchar("to_number"),
  callType: varchar("call_type").default("inbound"), // inbound, outbound
  transcript: text("transcript"),
  summary: text("summary"),
  duration: integer("duration"),
  callStatus: text("call_status").default("completed"),
  recording: text("recording"), // URL to call recording
  sentimentScore: real("sentiment_score"),
  appointmentBooked: boolean("appointment_booked").default(false),
  appointmentId: uuid("appointment_id").references(() => appointments.id),
  cost: real("cost").default(0.0000),
  exportedToFile: boolean("exported_to_file").notNull().default(false),
  fileExportPath: text("file_export_path"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Appointments table
export const appointments: any = pgTable("appointments", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").references(() => clinics.id, { onDelete: "cascade" }),
  callLogId: uuid("call_log_id").references(() => callLogs.id),
  patientName: text("patient_name").notNull(),
  patientPhone: text("patient_phone"),
  patientEmail: varchar("patient_email"),
  appointmentDate: timestamp("appointment_date").notNull(),
  appointmentType: text("appointment_type"),
  duration: integer("duration").default(30), // minutes
  status: text("status").default("scheduled"), // scheduled, confirmed, cancelled, completed, no_show
  notes: text("notes"),
  price: real("price"),
  isPaid: boolean("is_paid").notNull().default(false),
  reminderSent: boolean("reminder_sent").notNull().default(false),
  exportedToFile: boolean("exported_to_file").notNull().default(false),
  fileExportPath: text("file_export_path"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// AI configurations table
export const aiConfigurations = pgTable("ai_configurations", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").references(() => clinics.id, { onDelete: "cascade" }),
  greetingMessage: text("greeting_message").default("Hello! Thank you for calling. How can I help you today?"),
  businessHours: jsonb("business_hours").default({
    monday: { open: "09:00", close: "17:00" },
    tuesday: { open: "09:00", close: "17:00" },
    wednesday: { open: "09:00", close: "17:00" },
    thursday: { open: "09:00", close: "17:00" },
    friday: { open: "09:00", close: "17:00" },
    saturday: { closed: true },
    sunday: { closed: true }
  }),
  services: jsonb("services").default([]),
  personalityTraits: text("personality_traits").default("professional, empathetic, helpful"),
  elevenlabsApiKey: text("elevenlabs_api_key"),
  elevenlabsVoiceId: text("elevenlabs_voice_id"),
  twilioAccountSid: text("twilio_account_sid"),
  twilioAuthToken: text("twilio_auth_token"),
  twilioPhoneNumber: text("twilio_phone_number"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// API configurations table
export const apiConfigurations = pgTable("api_configurations", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").references(() => clinics.id, { onDelete: "cascade" }),
  googleServiceAccountKey: jsonb("google_service_account_key"),
  elevenlabsApiKey: text("elevenlabs_api_key"),
  elevenlabsVoiceId: text("elevenlabs_voice_id"),
  twilioAccountSid: text("twilio_account_sid"),
  twilioAuthToken: text("twilio_auth_token"),
  twilioPhoneNumber: text("twilio_phone_number"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Schema exports
export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertClinicSchema = createInsertSchema(clinics).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertCallLogSchema = createInsertSchema(callLogs).omit({
  id: true,
  createdAt: true,
});

export const insertAppointmentSchema = createInsertSchema(appointments).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertAiConfigurationSchema = createInsertSchema(aiConfigurations).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertApiConfigurationSchema = createInsertSchema(apiConfigurations).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

// Platform analytics for admin dashboard
export const platformAnalytics = pgTable("platform_analytics", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  date: timestamp("date").notNull(),
  totalClinics: integer("total_clinics").notNull().default(0),
  activeClinics: integer("active_clinics").notNull().default(0),
  totalCalls: integer("total_calls").notNull().default(0),
  totalAppointments: integer("total_appointments").notNull().default(0),
  totalRevenue: decimal("total_revenue", { precision: 12, scale: 2 }).notNull().default("0.00"),
  newSignups: integer("new_signups").notNull().default(0),
  churnedClinics: integer("churned_clinics").notNull().default(0),
  averageCallDuration: integer("average_call_duration").default(0),
  createdAt: timestamp("created_at").defaultNow(),
});

// File exports tracking
export const fileExports = pgTable("file_exports", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").notNull().references(() => clinics.id, { onDelete: "cascade" }),
  exportType: varchar("export_type").notNull(), // appointments, calls, analytics
  fileName: varchar("file_name").notNull(),
  filePath: text("file_path").notNull(),
  fileFormat: varchar("file_format").notNull().default("csv"), // csv, xlsx, json
  recordCount: integer("record_count").notNull().default(0),
  fileSize: integer("file_size"), // bytes
  downloadCount: integer("download_count").notNull().default(0),
  expiresAt: timestamp("expires_at"),
  createdAt: timestamp("created_at").defaultNow(),
});

// Compliance and Audit Logging Tables
export const auditLogs = pgTable("audit_logs", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").references(() => clinics.id, { onDelete: "cascade" }),
  userId: varchar("user_id").notNull().references(() => users.id),
  action: varchar("action").notNull(), // CREATE, READ, UPDATE, DELETE, EXPORT, LOGIN, etc.
  entityType: varchar("entity_type").notNull(), // user, clinic, appointment, call_log, etc.
  entityId: varchar("entity_id"), // ID of the affected entity
  details: jsonb("details"), // Additional context about the action
  ipAddress: varchar("ip_address"),
  userAgent: text("user_agent"),
  successful: boolean("successful").notNull().default(true),
  errorMessage: text("error_message"),
  timestamp: timestamp("timestamp").notNull().defaultNow(),
  retentionDate: timestamp("retention_date"), // When this log should be deleted
});

export const dataRetentionPolicies = pgTable("data_retention_policies", {
  id: serial("id").primaryKey(),
  dataType: varchar("data_type").notNull().unique(), // call_logs, appointments, audit_logs, etc.
  retentionPeriodDays: integer("retention_period_days").notNull(),
  isActive: boolean("is_active").notNull().default(true),
  description: text("description"),
  legalBasis: varchar("legal_basis"), // HIPAA, GDPR, business_requirement
  lastProcessed: timestamp("last_processed"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const consentRecords = pgTable("consent_records", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").notNull().references(() => clinics.id, { onDelete: "cascade" }),
  patientIdentifier: varchar("patient_identifier"), // Phone number or anonymized ID
  consentType: varchar("consent_type").notNull(), // data_processing, marketing, recording
  consentStatus: boolean("consent_status").notNull(), // true = granted, false = withdrawn
  consentMethod: varchar("consent_method"), // verbal, written, digital
  consentVersion: varchar("consent_version"), // Version of terms they consented to
  grantedAt: timestamp("granted_at"),
  withdrawnAt: timestamp("withdrawn_at"),
  expiresAt: timestamp("expires_at"),
  legalBasis: varchar("legal_basis"), // legitimate_interest, consent, contract
  processingPurpose: text("processing_purpose"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const dataBreachIncidents = pgTable("data_breach_incidents", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  incidentId: varchar("incident_id").notNull().unique(), // Generated incident reference
  severity: varchar("severity").notNull(), // low, medium, high, critical
  status: varchar("status").notNull().default("open"), // open, investigating, resolved, closed
  affectedClinics: text("affected_clinics").array(), // Array of clinic IDs
  affectedDataTypes: text("affected_data_types").array(), // personal, health, financial
  estimatedRecordsAffected: integer("estimated_records_affected"),
  detectionMethod: varchar("detection_method"), // automated, manual, external_report
  incidentDescription: text("incident_description").notNull(),
  rootCause: text("root_cause"),
  remediationSteps: text("remediation_steps"),
  notificationRequired: boolean("notification_required").default(false),
  notificationsSent: jsonb("notifications_sent"), // Track regulatory notifications
  reportedToAuthorities: boolean("reported_to_authorities").default(false),
  reportedAt: timestamp("reported_at"),
  resolvedAt: timestamp("resolved_at"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const vendorAgreements = pgTable("vendor_agreements", {
  id: serial("id").primaryKey(),
  vendorName: varchar("vendor_name").notNull(),
  agreementType: varchar("agreement_type").notNull(), // BAA, DPA, service_agreement
  status: varchar("status").notNull().default("active"), // active, expired, terminated
  signedDate: timestamp("signed_date").notNull(),
  expiryDate: timestamp("expiry_date"),
  renewalDate: timestamp("renewal_date"),
  dataProcessingActivities: text("data_processing_activities").array(),
  complianceFrameworks: text("compliance_frameworks").array(), // HIPAA, GDPR, SOC2
  contractDocument: text("contract_document_path"),
  keyContacts: jsonb("key_contacts"), // Contact information
  auditSchedule: varchar("audit_schedule"), // annual, biannual, quarterly
  lastAuditDate: timestamp("last_audit_date"),
  nextAuditDate: timestamp("next_audit_date"),
  riskAssessment: varchar("risk_assessment"), // low, medium, high
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Relations
export const usersRelations = relations(users, ({ many }) => ({
  clinics: many(clinics),
}));

export const clinicsRelations = relations(clinics, ({ one, many }) => ({
  owner: one(users, {
    fields: [clinics.ownerId],
    references: [users.id],
  }),
  appointments: many(appointments),
  callLogs: many(callLogs),
  aiConfiguration: one(aiConfigurations),
  apiConfiguration: one(apiConfigurations),
  fileExports: many(fileExports),
}));

export const appointmentsRelations = relations(appointments, ({ one }): any => ({
  clinic: one(clinics, {
    fields: [appointments.clinicId],
    references: [clinics.id],
  }),
  call: one(callLogs, {
    fields: [appointments.callLogId],
    references: [callLogs.id],
  }),
}));

export const callLogsRelations = relations(callLogs, ({ one }): any => ({
  clinic: one(clinics, {
    fields: [callLogs.clinicId],
    references: [clinics.id],
  }),
  appointment: one(appointments, {
    fields: [callLogs.appointmentId],
    references: [appointments.id],
  }),
}));

export const aiConfigurationsRelations = relations(aiConfigurations, ({ one }) => ({
  clinic: one(clinics, {
    fields: [aiConfigurations.clinicId],
    references: [clinics.id],
  }),
}));

export const apiConfigurationsRelations = relations(apiConfigurations, ({ one }) => ({
  clinic: one(clinics, {
    fields: [apiConfigurations.clinicId],
    references: [clinics.id],
  }),
}));

export const fileExportsRelations = relations(fileExports, ({ one }) => ({
  clinic: one(clinics, {
    fields: [fileExports.clinicId],
    references: [clinics.id],
  }),
}));

// Organization membership table for multi-user RBAC
export const clinicMembers = pgTable("clinic_members", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id, { onDelete: "cascade" }),
  clinicId: uuid("clinic_id").notNull().references(() => clinics.id, { onDelete: "cascade" }),
  role: varchar("role").notNull().default("staff"), // owner, admin, manager, staff, viewer
  permissions: jsonb("permissions").default({
    canManageSettings: false,
    canManageTeam: false,
    canViewAnalytics: false,
    canMakeCalls: false,
    canViewCallLogs: false,
    canManageAppointments: false,
    canConfigureAI: false,
    canExportData: false
  }),
  invitedBy: varchar("invited_by").references(() => users.id),
  invitedAt: timestamp("invited_at").defaultNow(),
  joinedAt: timestamp("joined_at"),
  isActive: boolean("is_active").notNull().default(true),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Team invitations table
export const teamInvitations = pgTable("team_invitations", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").notNull().references(() => clinics.id, { onDelete: "cascade" }),
  email: varchar("email").notNull(),
  role: varchar("role").notNull().default("staff"),
  permissions: jsonb("permissions").default({
    canManageSettings: false,
    canManageTeam: false,
    canViewAnalytics: false,
    canMakeCalls: false,
    canViewCallLogs: false,
    canManageAppointments: false,
    canConfigureAI: false,
    canExportData: false
  }),
  token: varchar("token").notNull().unique(),
  invitedBy: varchar("invited_by").notNull().references(() => users.id),
  status: varchar("status").notNull().default("pending"), // pending, accepted, expired
  expiresAt: timestamp("expires_at").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Usage tracking table for quotas and billing
export const usageTracking = pgTable("usage_tracking", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").notNull().references(() => clinics.id, { onDelete: "cascade" }),
  period: varchar("period").notNull(), // YYYY-MM format
  callsCount: integer("calls_count").notNull().default(0),
  callMinutes: integer("call_minutes").notNull().default(0),
  aiCharactersUsed: integer("ai_characters_used").notNull().default(0),
  appointmentsBooked: integer("appointments_booked").notNull().default(0),
  resetAt: timestamp("reset_at").defaultNow().notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// API Keys table for tenant-scoped API access
export const apiKeys = pgTable("api_keys", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").references(() => clinics.id, { onDelete: "cascade" }),
  keyName: text("key_name").notNull(),
  keyPrefix: varchar("key_prefix", { length: 12 }).notNull().unique(), // pk_live_1234567890ab
  hashedKey: text("hashed_key").notNull(), // bcrypt hash of full key
  permissions: text("permissions").array().notNull().default(sql`ARRAY[]::text[]`), // ['read:appointments', 'write:calls']
  environment: varchar("environment").notNull().default("live"), // live, test
  status: varchar("status").notNull().default("active"), // active, revoked, expired
  lastUsedAt: timestamp("last_used_at"),
  expiresAt: timestamp("expires_at"),
  createdBy: varchar("created_by").notNull().references(() => users.id),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  revokedAt: timestamp("revoked_at"),
  revokedBy: varchar("revoked_by").references(() => users.id),
});

// Webhooks table for event notifications
export const webhooks = pgTable("webhooks", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").references(() => clinics.id, { onDelete: "cascade" }),
  url: text("url").notNull(),
  description: text("description"),
  events: text("events").array().notNull().default(sql`ARRAY[]::text[]`), // ['call.completed', 'appointment.created']
  secret: text("secret").notNull(), // For signature verification
  status: varchar("status").notNull().default("active"), // active, disabled, failed
  retryPolicy: jsonb("retry_policy").default(sql`'{"maxRetries": 3, "backoffMultiplier": 2}'::jsonb`),
  lastSuccessAt: timestamp("last_success_at"),
  lastFailureAt: timestamp("last_failure_at"),
  consecutiveFailures: integer("consecutive_failures").notNull().default(0),
  createdBy: varchar("created_by").notNull().references(() => users.id),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Webhook deliveries for tracking and debugging
export const webhookDeliveries = pgTable("webhook_deliveries", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  webhookId: uuid("webhook_id").references(() => webhooks.id, { onDelete: "cascade" }),
  eventType: varchar("event_type").notNull(),
  payload: jsonb("payload").notNull(),
  status: varchar("status").notNull(), // pending, delivered, failed, retrying
  httpStatus: integer("http_status"),
  responseBody: text("response_body"),
  responseHeaders: jsonb("response_headers"),
  attemptCount: integer("attempt_count").notNull().default(0),
  maxAttempts: integer("max_attempts").notNull().default(3),
  nextRetryAt: timestamp("next_retry_at"),
  deliveredAt: timestamp("delivered_at"),
  failedAt: timestamp("failed_at"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// API usage metrics for rate limiting and analytics
export const apiUsage = pgTable("api_usage", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").references(() => clinics.id, { onDelete: "cascade" }),
  apiKeyId: uuid("api_key_id").references(() => apiKeys.id, { onDelete: "set null" }),
  endpoint: varchar("endpoint").notNull(),
  method: varchar("method").notNull(),
  statusCode: integer("status_code").notNull(),
  responseTime: integer("response_time"), // milliseconds
  requestSize: integer("request_size"), // bytes
  responseSize: integer("response_size"), // bytes
  userAgent: text("user_agent"),
  ipAddress: varchar("ip_address"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Rate limiting buckets
export const rateLimitBuckets = pgTable("rate_limit_buckets", {
  id: varchar("id").primaryKey(), // Composite: clinicId:endpoint:window
  requestCount: integer("request_count").notNull().default(0),
  windowStart: timestamp("window_start").notNull(),
  windowEnd: timestamp("window_end").notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Support Tickets table for customer success portal
export const supportTickets = pgTable("support_tickets", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").references(() => clinics.id, { onDelete: "cascade" }),
  userId: varchar("user_id").references(() => users.id, { onDelete: "set null" }),
  title: text("title").notNull(),
  description: text("description").notNull(),
  priority: varchar("priority").notNull().default("medium"), // low, medium, high, urgent
  category: varchar("category").notNull().default("general"), // general, technical, billing, onboarding
  status: varchar("status").notNull().default("open"), // open, in_progress, waiting_customer, resolved, closed
  assignedTo: varchar("assigned_to").references(() => users.id, { onDelete: "set null" }),
  tags: text("tags").array().default(sql`ARRAY[]::text[]`),
  satisfaction: integer("satisfaction"), // 1-5 rating after resolution
  satisfactionFeedback: text("satisfaction_feedback"),
  firstResponseAt: timestamp("first_response_at"),
  resolvedAt: timestamp("resolved_at"),
  closedAt: timestamp("closed_at"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Ticket Comments/Responses table
export const ticketComments = pgTable("ticket_comments", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  ticketId: uuid("ticket_id").references(() => supportTickets.id, { onDelete: "cascade" }),
  userId: varchar("user_id").references(() => users.id, { onDelete: "set null" }),
  content: text("content").notNull(),
  isInternal: boolean("is_internal").notNull().default(false), // Internal notes vs customer messages
  attachmentUrls: text("attachment_urls").array().default(sql`ARRAY[]::text[]`),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Onboarding Checklists table
export const onboardingChecklists = pgTable("onboarding_checklists", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").references(() => clinics.id, { onDelete: "cascade" }),
  templateName: varchar("template_name").notNull().default("standard"), // standard, enterprise, custom
  progress: jsonb("progress").notNull().default(sql`'{}'::jsonb`), // Track completion status
  currentStep: integer("current_step").notNull().default(0),
  totalSteps: integer("total_steps").notNull().default(10),
  completedAt: timestamp("completed_at"),
  assignedTo: varchar("assigned_to").references(() => users.id, { onDelete: "set null" }),
  notes: text("notes"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Customer Health Scores table
export const customerHealthScores = pgTable("customer_health_scores", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").references(() => clinics.id, { onDelete: "cascade" }),
  period: varchar("period").notNull(), // YYYY-MM format
  overallScore: integer("overall_score").notNull(), // 0-100
  usageScore: integer("usage_score").notNull(), // API/feature usage
  engagementScore: integer("engagement_score").notNull(), // Login frequency, feature adoption
  supportScore: integer("support_score").notNull(), // Ticket resolution satisfaction
  retentionRisk: varchar("retention_risk").notNull().default("low"), // low, medium, high
  lastActivityAt: timestamp("last_activity_at"),
  actionItems: text("action_items").array().default(sql`ARRAY[]::text[]`),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// System Status Incidents table for status page
export const systemIncidents = pgTable("system_incidents", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description").notNull(),
  status: varchar("status").notNull().default("investigating"), // investigating, identified, monitoring, resolved
  impact: varchar("impact").notNull().default("minor"), // minor, major, critical
  affectedServices: text("affected_services").array().notNull(), // ['API', 'Dashboard', 'Voice Calls']
  startedAt: timestamp("started_at").notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
  resolvedAt: timestamp("resolved_at"),
  postmortemUrl: text("postmortem_url"),
  createdBy: varchar("created_by").notNull().references(() => users.id),
});

// Incident Updates table
export const incidentUpdates = pgTable("incident_updates", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  incidentId: uuid("incident_id").references(() => systemIncidents.id, { onDelete: "cascade" }),
  message: text("message").notNull(),
  status: varchar("status").notNull(), // investigating, identified, monitoring, resolved
  createdBy: varchar("created_by").notNull().references(() => users.id),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Customer Success Metrics table
export const customerSuccessMetrics = pgTable("customer_success_metrics", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  clinicId: uuid("clinic_id").references(() => clinics.id, { onDelete: "cascade" }),
  metricType: varchar("metric_type").notNull(), // nps, csat, ces, churn_risk
  value: real("value").notNull(),
  period: varchar("period").notNull(), // YYYY-MM or YYYY-MM-DD
  context: jsonb("context"), // Additional metric context
  collectedAt: timestamp("collected_at").defaultNow().notNull(),
});

// Relations for new tables
export const clinicMembersRelations = relations(clinicMembers, ({ one }) => ({
  user: one(users, {
    fields: [clinicMembers.userId],
    references: [users.id],
  }),
  clinic: one(clinics, {
    fields: [clinicMembers.clinicId],
    references: [clinics.id],
  }),
}));

export const teamInvitationsRelations = relations(teamInvitations, ({ one }) => ({
  clinic: one(clinics, {
    fields: [teamInvitations.clinicId],
    references: [clinics.id],
  }),
}));

export const usageTrackingRelations = relations(usageTracking, ({ one }) => ({
  clinic: one(clinics, {
    fields: [usageTracking.clinicId],
    references: [clinics.id],
  }),
}));

export const apiKeysRelations = relations(apiKeys, ({ one }) => ({
  clinic: one(clinics, {
    fields: [apiKeys.clinicId],
    references: [clinics.id],
  }),
  createdBy: one(users, {
    fields: [apiKeys.createdBy],
    references: [users.id],
  }),
}));

export const webhooksRelations = relations(webhooks, ({ one, many }) => ({
  clinic: one(clinics, {
