router.post("/api/twilio/send-sms", 
  requireAuth,
  createRateLimit(60 * 1000, 10), // 10 SMS per minute
  auditLog('twilio_send_sms'),
  async (req: any, res) => {
  try {
    const { clinicId, to, message } = req.body;

    if (!clinicId || !to || !message) {
      return res.status(400).json({ 
        message: "Clinic ID, recipient, and message are required" 
      });
    }

    // Verify clinic ownership
    const userId = req.user.claims.sub;
    const [clinic] = await db.select().from(clinics).where(eq(clinics.id, clinicId));

    if (!clinic || clinic.ownerId !== userId) {
      return res.status(403).json({ message: "Unauthorized access to clinic" });
    }

    // Get API configuration
    const [config] = await db
      .select()
      .from(apiConfigurations)
      .where(eq(apiConfigurations.clinicId, clinicId));

    if (!config?.twilioAccountSid || !config?.twilioAuthToken) {
      return res.status(400).json({ 
        message: "Twilio not configured for this clinic" 
      });
    }

    // Validate phone number format
    const phoneRegex = /^\+[1-9]\d{1,14}$/;
    if (!phoneRegex.test(to)) {
      return res.status(400).json({ 
        message: "Invalid phone number format. Use E.164 format (e.g., +441234567890)" 
      });
    }

    // Validate message length (SMS limit is 160 characters)
    if (message.length > 1600) { // Allow up to 10 SMS segments
      return res.status(400).json({ 
        message: "Message too long. Maximum 1600 characters allowed." 
      });
    }

    // Decrypt auth token for use (handle both encrypted and legacy plaintext)
    let decryptedAuthToken;
    try {
      decryptedAuthToken = EncryptionService.decrypt(config.twilioAuthToken);
    } catch {
      // Fallback for legacy plaintext tokens
      decryptedAuthToken = config.twilioAuthToken;
    }

    // Validate Twilio phone number (from number) for SMS
    if (config.twilioPhoneNumber && !/^\+[1-9]\d{1,14}$/.test(config.twilioPhoneNumber)) {
      return res.status(400).json({ 
        message: "Invalid Twilio phone number configuration. Please check your settings." 
      });
    }

    const twilio = new TwilioService({
      accountSid: config.twilioAccountSid,
      authToken: decryptedAuthToken,
      phoneNumber: config.twilioPhoneNumber || ''
    });

    const result = await twilio.sendSMS(to, message);
    const messageSid = result.messageSid;

    res.json({ messageSid, success: true });
  } catch (error) {
    console.error("Error sending SMS:", error);
    if (error instanceof Error && error.message.includes('phone number')) {
      return res.status(400).json({ 
        message: "Invalid phone number or SMS service unavailable" 
      });
    }
    res.status(500).json({ message: "Failed to send SMS" });
  }
});

// Twilio Call Placement with quota enforcement
router.post("/api/twilio/make-call", 
  requireAuth,
  createRateLimit(60 * 1000, 5), // 5 calls per minute
  auditLog('twilio_make_call'),
  async (req: any, res) => {
  try {
    const { clinicId, to } = req.body;

    if (!clinicId || !to) {
      return res.status(400).json({ 
        message: "Clinic ID and phone number are required" 
      });
    }

    // Verify clinic ownership
    const userId = req.user.claims.sub;
    const [clinic] = await db.select().from(clinics).where(eq(clinics.id, clinicId));

    if (!clinic || clinic.ownerId !== userId) {
      return res.status(403).json({ message: "Unauthorized access to clinic" });
    }

    // Check subscription quotas before making call
    const subscriptionLimits = {
      essential: { calls: 1000 },
      professional: { calls: 2500 },
      enterprise: { calls: -1 } // unlimited
    };

    const limits = subscriptionLimits[clinic.subscriptionTier as keyof typeof subscriptionLimits] || subscriptionLimits.essential;

    if (limits.calls !== -1 && clinic.monthlyCallsUsed >= limits.calls) {
      return res.status(429).json({
        message: `Monthly call quota (${limits.calls}) exceeded. Upgrade your plan to continue.`,
        currentUsage: clinic.monthlyCallsUsed,
        limit: limits.calls,
        subscriptionTier: clinic.subscriptionTier,
        upgradeRequired: true
      });
    }

    // Get API configuration
    const [config] = await db
      .select()
      .from(apiConfigurations)
      .where(eq(apiConfigurations.clinicId, clinicId));

    if (!config?.twilioAccountSid || !config?.twilioAuthToken) {
      return res.status(400).json({ 
        message: "Twilio not configured for this clinic" 
      });
    }

    // Decrypt auth token for use (handle both encrypted and legacy plaintext)
    let decryptedAuthToken;
    try {
      decryptedAuthToken = EncryptionService.decrypt(config.twilioAuthToken);
    } catch {
      // Fallback for legacy plaintext tokens
      decryptedAuthToken = config.twilioAuthToken;
    }

    // Validate Twilio phone number (from number)
    if (!config.twilioPhoneNumber) {
      return res.status(400).json({ 
        message: "Twilio phone number not configured for this clinic" 
      });
    }

    // Validate from phone number
    if (!/^\+[1-9]\d{1,14}$/.test(config.twilioPhoneNumber)) {
      return res.status(400).json({ 
        message: "Invalid Twilio phone number configuration. Please check your settings." 
      });
    }

    // Validate to phone number format
    if (!/^\+[1-9]\d{1,14}$/.test(to)) {
      return res.status(400).json({ 
        message: "Invalid phone number format. Use E.164 format (e.g., +441234567890)" 
      });
    }

    const twilio = new TwilioService({
      accountSid: config.twilioAccountSid,
      authToken: decryptedAuthToken,
      phoneNumber: config.twilioPhoneNumber || ''
    });

    // Create webhook URL for this clinic
    const baseUrl = process.env.PUBLIC_BASE_URL || `${req.protocol}://${req.get('host')}`;
    const webhookUrl = `${baseUrl}/api/twilio/webhook?clinicId=${clinicId}`;

    const result = await twilio.makeCall(to, webhookUrl);

    // Track usage: increment call count for the clinic
    try {
      await db
        .update(clinics)
        .set({ 
          monthlyCallsUsed: clinic.monthlyCallsUsed + 1,
          totalCalls: clinic.totalCalls + 1,
          updatedAt: new Date()
        })
        .where(eq(clinics.id, clinicId));
    } catch (trackingError) {
      console.error("Error tracking call usage:", trackingError);
      // Don't fail the request if usage tracking fails
    }

    res.json({ 
      callSid: result.callSid,
      status: result.status,
      to: result.to,
      from: result.from,
      success: true 
    });
  } catch (error) {
    console.error("Error making call:", error);
    if (error instanceof Error && error.message.includes('phone number')) {
      return res.status(400).json({ 
        message: "Invalid phone number or calling service unavailable" 
      });
    }
    res.status(500).json({ message: "Failed to make call" });
  }
});

// API Configuration Management
router.post("/api/configurations", 
  requireAuth,
  createRateLimit(60 * 1000, 10), // 10 requests per minute
  auditLog('api_config_update'),
  async (req: any, res) => {
  try {
    const { clinicId, ...configData } = req.body;

    if (!clinicId) {
      return res.status(400).json({ 
        message: "Clinic ID is required" 
      });
    }

    // Verify clinic ownership
    const userId = req.user.claims.sub;
    const [clinic] = await db.select().from(clinics).where(eq(clinics.id, clinicId));

    if (!clinic || clinic.ownerId !== userId) {
      return res.status(403).json({ message: "Unauthorized access to clinic" });
    }

    // Encrypt sensitive fields
    const encryptedConfigData = { ...configData };
    if (configData.twilioAuthToken) {
      encryptedConfigData.twilioAuthToken = EncryptionService.encrypt(configData.twilioAuthToken);
    }
    if (configData.elevenlabsApiKey) {
      encryptedConfigData.elevenlabsApiKey = EncryptionService.encrypt(configData.elevenlabsApiKey);
    }

    // Check if configuration exists
    const [existingConfig] = await db
      .select()
      .from(apiConfigurations)
      .where(eq(apiConfigurations.clinicId, clinicId));

    if (existingConfig) {
      // Update existing configuration
      await db
        .update(apiConfigurations)
        .set({ ...encryptedConfigData, updatedAt: new Date() })
        .where(eq(apiConfigurations.clinicId, clinicId));
    } else {
      // Create new configuration
      await db.insert(apiConfigurations).values({
        clinicId,
        ...encryptedConfigData,
      });
    }

    res.json({ success: true });
  } catch (error) {
    console.error("Error saving API configuration:", error);
    res.status(500).json({ message: "Failed to save configuration" });
  }
});

router.get("/api/configurations/:clinicId", 
  requireAuth,
  createRateLimit(60 * 1000, 20), // 20 requests per minute
  auditLog('api_config_read'),
  async (req: any, res) => {
  try {
    const { clinicId } = req.params;

    // Verify clinic ownership
    const userId = req.user.claims.sub;
    const [clinic] = await db.select().from(clinics).where(eq(clinics.id, clinicId));

    if (!clinic || clinic.ownerId !== userId) {
      return res.status(403).json({ message: "Unauthorized access to clinic" });
    }

    const [config] = await db
      .select()
      .from(apiConfigurations)
      .where(eq(apiConfigurations.clinicId, clinicId));

    if (!config) {
      return res.status(404).json({ 
        message: "Configuration not found" 
      });
    }

    // Don't return sensitive data in full
    const safeConfig = {
      ...config,
      googleServiceAccountKey: config.googleServiceAccountKey ? "***configured***" : null,
      elevenlabsApiKey: config.elevenlabsApiKey ? "***configured***" : null,
      twilioAuthToken: config.twilioAuthToken ? "***configured***" : null,
    };

    res.json(safeConfig);
  } catch (error) {
    console.error("Error fetching API configuration:", error);
    res.status(500).json({ message: "Failed to fetch configuration" });
  }
});

// Contextual Help System
const contextualHelpSchema = z.object({
  question: z.string().min(1).max(500),
  context: z.string().max(200),
  page: z.string().max(50),
});

router.post("/api/help/contextual", 
  requireAuth,
  createRateLimit(60 * 1000, 10), // 10 requests per minute
  async (req: any, res) => {
  try {
    const validatedData = contextualHelpSchema.parse(req.body);
    const { question, context, page } = validatedData;

    const helpService = new ContextualHelpService();
    const response = await helpService.getContextualHelp({
      question,
      context,
      page,
      userId: req.user?.claims?.sub
    });

    // Audit log for help requests
    console.log(`[HELP] User ${req.user?.claims?.sub} asked: "${question}" on page: ${page}`);

    res.json(response);
  } catch (error) {
    console.error("Error providing contextual help:", error);
    res.status(500).json({ 
      message: "Failed to provide help response",
      error: error instanceof Error ? error.message : "Unknown error"
    });
  }
});

export default router;

server/services/elevenlabs.ts.content:
import { ElevenLabs } from 'elevenlabs';

export class ElevenLabsService {
  private elevenlabs: ElevenLabs;

  constructor(apiKey: string) {
    if (!apiKey) {
      throw new Error('ElevenLabs API key is required');
    }

    this.elevenlabs = new ElevenLabs({
      apiKey: apiKey,
    });
  }

  async generateSpeech(text: string, voiceId: string): Promise<Buffer> {
    try {
      const audio = await this.elevenlabs.generate({
        voice: voiceId,
        text: text,
        model_id: "eleven_monolingual_v1"
      });

      const chunks: Buffer[] = [];
      for await (const chunk of audio) {
        chunks.push(chunk);
      }

      return Buffer.concat(chunks);
    } catch (error) {
      console.error('Error generating speech with ElevenLabs:', error);
      throw new Error('Failed to generate speech');
    }
  }

  async getVoices(): Promise<any[]> {
    try {
      const voices = await this.elevenlabs.voices.getAll();
      return voices.voices || [];
    } catch (error) {
      console.error('Error fetching voices from ElevenLabs:', error);
      throw new Error('Failed to fetch voices');
    }
  }

  async cloneVoice(audioFile: Buffer, name: string, description?: string): Promise<string> {
    try {
      const voice = await this.elevenlabs.voices.add({
        name: name,
        description: description || '',
        files: [audioFile],
      });

      return voice.voice_id;
    } catch (error) {
      console.error('Error cloning voice with ElevenLabs:', error);
      throw new Error('Failed to clone voice');
    }
  }

  async getVoiceSettings(voiceId: string): Promise<any> {
    try {
      const settings = await this.elevenlabs.voices.getSettings(voiceId);
      return settings;
    } catch (error) {
      console.error('Error getting voice settings from ElevenLabs:', error);
      throw new Error('Failed to get voice settings');
    }
  }
}

server/services/fileExport.ts.content:
import { writeFile, mkdir } from "fs/promises";
import { join } from "path";
import { db } from "../db";
import { appointments, callLogs, fileExports, clinics } from "@shared/schema";
import { eq, and, gte, lte } from "drizzle-orm";
import type { Appointment, CallLog, Clinic } from "@shared/schema";

export class FileExportService {
  private baseExportPath = "exports";

  constructor() {
    // Ensure export directory exists
    this.ensureExportDirectory();
  }

  private async ensureExportDirectory() {
    try {
      await mkdir(this.baseExportPath, { recursive: true });
    } catch (error) {
      // Directory might already exist
    }
  }

  async exportAppointments(clinicId: string, format: "csv" | "json" = "csv", dateRange?: { start: Date; end: Date }) {
    // Fetch appointments for the clinic
    let query = db.select().from(appointments).where(eq(appointments.clinicId, clinicId));

    if (dateRange) {
      query = query.where(
        and(
          eq(appointments.clinicId, clinicId),
          gte(appointments.appointmentDate, dateRange.start),
          lte(appointments.appointmentDate, dateRange.end)
        )
      );
    }

    const appointmentData = await query;

    if (appointmentData.length === 0) {
      throw new Error("No appointments found for the specified criteria");
    }

    const fileName = `appointments-${clinicId}-${new Date().toISOString().split('T')[0]}.${format}`;
    const filePath = join(this.baseExportPath, fileName);

    let fileContent: string;

    if (format === "csv") {
      fileContent = this.convertAppointmentsToCSV(appointmentData);
    } else {
      fileContent = JSON.stringify(appointmentData, null, 2);
    }

    await writeFile(filePath, fileContent, 'utf8');

    // Record the export in the database
    await this.recordFileExport(clinicId, "appointments", fileName, filePath, format, appointmentData.length);

    // Update appointments as exported
    await db.update(appointments)
      .set({ exportedToFile: true, fileExportPath: filePath })
      .where(eq(appointments.clinicId, clinicId));

    return {
      fileName,
      filePath,
      recordCount: appointmentData.length,
      format
    };
  }

  async exportCallLogs(clinicId: string, format: "csv" | "json" = "csv", dateRange?: { start: Date; end: Date }) {
    let query = db.select().from(callLogs).where(eq(callLogs.clinicId, clinicId));

    if (dateRange) {
      query = query.where(
        and(
          eq(callLogs.clinicId, clinicId),
          gte(callLogs.createdAt, dateRange.start),
          lte(callLogs.createdAt, dateRange.end)
        )
      );
    }

    const callData = await query;

    if (callData.length === 0) {
      throw new Error("No call logs found for the specified criteria");
    }

    const fileName = `call-logs-${clinicId}-${new Date().toISOString().split('T')[0]}.${format}`;
    const filePath = join(this.baseExportPath, fileName);

    let fileContent: string;

    if (format === "csv") {
      fileContent = this.convertCallLogsToCSV(callData);
    } else {
      fileContent = JSON.stringify(callData, null, 2);
    }

    await writeFile(filePath, fileContent, 'utf8');

    // Record the export in the database
    await this.recordFileExport(clinicId, "calls", fileName, filePath, format, callData.length);

    // Update call logs as exported
    await db.update(callLogs)
      .set({ exportedToFile: true, fileExportPath: filePath })
      .where(eq(callLogs.clinicId, clinicId));

    return {
      fileName,
      filePath,
      recordCount: callData.length,
      format
    };
  }

  async exportClinicAnalytics(clinicId: string, format: "csv" | "json" = "csv") {
    // Get clinic data with aggregated statistics
    const [clinic] = await db.select().from(clinics).where(eq(clinics.id, clinicId));

    if (!clinic) {
      throw new Error("Clinic not found");
    }

    const [appointmentStats] = await db.select({
      totalAppointments: appointments.id,
      // Add more aggregated fields as needed
    }).from(appointments).where(eq(appointments.clinicId, clinicId));

    const [callStats] = await db.select({
      totalCalls: callLogs.id,
      // Add more aggregated fields as needed
    }).from(callLogs).where(eq(callLogs.clinicId, clinicId));

    const analyticsData = {
      clinic: {
        id: clinic.id,
        name: clinic.name,
        email: clinic.email,
        phoneNumber: clinic.phoneNumber,
        subscriptionTier: clinic.subscriptionTier,
        subscriptionStatus: clinic.subscriptionStatus,
        totalCalls: clinic.totalCalls,
        totalAppointments: clinic.totalAppointments,
        monthlyCallsUsed: clinic.monthlyCallsUsed,
        createdAt: clinic.createdAt,
      },
      statistics: {
        totalAppointments: appointmentStats?.totalAppointments || 0,
        totalCalls: callStats?.totalCalls || 0,
        // Add more analytics as needed
      },
      exportedAt: new Date().toISOString(),
    };

    const fileName = `analytics-${clinicId}-${new Date().toISOString().split('T')[0]}.${format}`;
    const filePath = join(this.baseExportPath, fileName);

    let fileContent: string;

    if (format === "csv") {
      fileContent = this.convertAnalyticsToCSV(analyticsData);
    } else {
      fileContent = JSON.stringify(analyticsData, null, 2);
    }

    await writeFile(filePath, fileContent, 'utf8');

    // Record the export in the database
    await this.recordFileExport(clinicId, "analytics", fileName, filePath, format, 1);

    return {
      fileName,
      filePath,
      recordCount: 1,
      format
    };
  }

  private async recordFileExport(
    clinicId: string,
    exportType: string,
    fileName: string,
    filePath: string,
    format: string,
    recordCount: number
  ) {
    await db.insert(fileExports).values({
      clinicId,
      exportType,
      fileName,
      filePath,
      fileFormat: format,
      recordCount,
      expiresAt: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000), // 30 days from now
    });
  }

  private convertAppointmentsToCSV(appointments: Appointment[]): string {
    const headers = [
      "ID",
      "Patient Name",
      "Patient Phone",
      "Patient Email",
      "Appointment Date",
      "Appointment Type",
      "Duration (mins)",
      "Status",
      "Price",
      "Is Paid",
      "Notes",
      "Created At"
    ];

    const rows = appointments.map(apt => [
      apt.id,
      apt.patientName,
      apt.patientPhone || "",
      apt.patientEmail || "",
      apt.appointmentDate.toISOString(),
      apt.appointmentType || "",
      apt.duration || "30",
      apt.status || "scheduled",
      apt.price || "0",
      apt.isPaid ? "Yes" : "No",
      (apt.notes || "").replace(/"/g, '""'), // Escape quotes for CSV
      apt.createdAt.toISOString()
    ]);

    return [headers.join(","), ...rows.map(row => row.map(cell => `"${cell}"`).join(","))].join("\n");
  }

  private convertCallLogsToCSV(callLogs: CallLog[]): string {
    const headers = [
      "ID",
      "Twilio Call SID",
      "Caller Phone",
      "To Number",
      "Call Type",
      "Duration (seconds)",
      "Call Status",
      "Sentiment Score",
      "Appointment Booked",
      "Cost",
      "Summary",
      "Created At"
    ];

    const rows = callLogs.map(call => [
      call.id,
      call.twilioCallSid || "",
      call.callerPhone,
      call.toNumber || "",
      call.callType || "inbound",
      call.duration || "0",
      call.callStatus || "completed",
      call.sentimentScore || "0",
      call.appointmentBooked ? "Yes" : "No",
      call.cost || "0",
      (call.summary || "").replace(/"/g, '""'), // Escape quotes for CSV
      call.createdAt.toISOString()
    ]);

    return [headers.join(","), ...rows.map(row => row.map(cell => `"${cell}"`).join(","))].join("\n");
  }

  private convertAnalyticsToCSV(data: any): string {
    const headers = [
      "Metric",
      "Value"
    ];

    const rows = [
      ["Clinic Name", data.clinic.name],
      ["Clinic Email", data.clinic.email],
      ["Phone Number", data.clinic.phoneNumber || ""],
      ["Subscription Tier", data.clinic.subscriptionTier],
      ["Subscription Status", data.clinic.subscriptionStatus],
      ["Total Calls", data.clinic.totalCalls.toString()],
      ["Total Appointments", data.clinic.totalAppointments.toString()],
      ["Monthly Calls Used", data.clinic.monthlyCallsUsed.toString()],
      ["Created At", data.clinic.createdAt],
      ["Exported At", data.exportedAt]
    ];

    return [headers.join(","), ...rows.map(row => `"${row[0]}","${row[1]}"`)].join("\n");
  }

  async getFileExports(clinicId: string) {
    return await db.select().from(fileExports)
      .where(eq(fileExports.clinicId, clinicId))
      .orderBy(fileExports.createdAt);
  }

  async deleteExpiredExports() {
    const now = new Date();
    const expiredExports = await db.select().from(fileExports)
      .where(lte(fileExports.expiresAt, now));

    // Delete files from filesystem and database records
    for (const exportRecord of expiredExports) {
      try {
        await import('fs/promises').then(fs => fs.unlink(exportRecord.filePath));
      } catch (error) {
        console.error(`Failed to delete file ${exportRecord.filePath}:`, error);
      }
    }

    await db.delete(fileExports).where(lte(fileExports.expiresAt, now));

    return expiredExports.length;
  }
}

server/services/googleSheets.ts.content:
import { google } from 'googleapis';
import type { Appointment } from '@shared/schema';

export class GoogleSheetsService {
  private sheets: any;
  private auth: any;

  constructor(serviceAccountKey: any) {
    if (!serviceAccountKey) {
      throw new Error('Google Service Account Key is required');
    }

    // Create JWT auth client
    this.auth = new google.auth.JWT({
      email: serviceAccountKey.client_email,
      key: serviceAccountKey.private_key,
      scopes: ['https://www.googleapis.com/auth/spreadsheets', 'https://www.googleapis.com/auth/drive.file']
    });

    this.sheets = google.sheets({ version: 'v4', auth: this.auth });
  }

  async createPatientSheet(clinicName: string): Promise<string> {
    try {
      // Create a new spreadsheet
      const response = await this.sheets.spreadsheets.create({
        resource: {
          properties: {
            title: `${clinicName} - Patient Records`,
          },
          sheets: [
            {
              properties: {
                title: 'Patient Appointments',
              },
            },
          ],
        },
      });

      const spreadsheetId = response.data.spreadsheetId;

      // Add headers to the sheet
      await this.sheets.spreadsheets.values.update({
        spreadsheetId,
        range: 'Patient Appointments!A1:H1',
        valueInputOption: 'RAW',
        resource: {
          values: [
            [
              'Patient Name',
              'Phone Number',
              'Appointment Date',
              'Appointment Type',
              'Status',
              'Notes',
              'Created At',
              'Updated At',
            ],
          ],
        },
      });

      return spreadsheetId;
    } catch (error) {
      console.error('Error creating Google Sheet:', error);
      throw new Error('Failed to create Google Sheet');
    }
  }

  async addPatientRecord(
    spreadsheetId: string,
    appointment: Appointment
  ): Promise<void> {
    try {
      const values = [
        [
          appointment.patientName,
          appointment.patientPhone || '',
          appointment.appointmentDate?.toISOString() || '',
          appointment.appointmentType || '',
          appointment.status || '',
          appointment.notes || '',
          appointment.createdAt?.toISOString() || '',
          appointment.updatedAt?.toISOString() || '',
        ],
      ];

      await this.sheets.spreadsheets.values.append({
        spreadsheetId,
        range: 'Patient Appointments!A:H',
        valueInputOption: 'RAW',
        resource: {
          values,
        },
      });
    } catch (error) {
      console.error('Error adding patient record to Google Sheet:', error);
      throw new Error('Failed to add patient record to Google Sheet');
    }
  }

  async updatePatientRecord(
    spreadsheetId: string,
    appointment: Appointment,
    rowIndex: number
  ): Promise<void> {
    try {
      const values = [
        [
          appointment.patientName,
          appointment.patientPhone || '',
          appointment.appointmentDate?.toISOString() || '',
          appointment.appointmentType || '',
          appointment.status || '',
          appointment.notes || '',
          appointment.createdAt?.toISOString() || '',
          appointment.updatedAt?.toISOString() || '',
        ],
      ];

      await this.sheets.spreadsheets.values.update({
        spreadsheetId,
        range: `Patient Appointments!A${rowIndex + 1}:H${rowIndex + 1}`,
        valueInputOption: 'RAW',
        resource: {
          values,
        },
      });
    } catch (error) {
      console.error('Error updating patient record in Google Sheet:', error);
      throw new Error('Failed to update patient record in Google Sheet');
    }
  }

  async shareSheet(spreadsheetId: string, email: string): Promise<void> {
    try {
      const drive = google.drive({ version: 'v3', auth: this.auth });

      await drive.permissions.create({
        fileId: spreadsheetId,
        requestBody: {
          role: 'writer',
          type: 'user',
          emailAddress: email,
        },
      });
    } catch (error) {
      console.error('Error sharing Google Sheet:', error);
      throw new Error('Failed to share Google Sheet');
    }
  }
}

server/services/twilio.ts.content:
import twilio from 'twilio';

export interface TwilioConfig {
  accountSid: string;
  authToken: string;
  phoneNumber: string;
}

export class TwilioService {
  private client: twilio.Twilio;

  constructor(private config: TwilioConfig) {
    this.client = twilio(config.accountSid, config.authToken);
  }

  // Make an outbound call
  async makeCall(to: string, webhookUrl: string) {
    try {
      const call = await this.client.calls.create({
        url: webhookUrl, // TwiML webhook URL
        to: to,
        from: this.config.phoneNumber,
        record: true, // Enable call recording
        recordingStatusCallback: `${webhookUrl}/recording-status`,
        statusCallback: `${webhookUrl}/call-status`,
        statusCallbackEvent: ['initiated', 'ringing', 'answered', 'completed'],
        timeout: 30, // Ring timeout in seconds
      });

      return {
        callSid: call.sid,
        status: call.status,
        to: call.to,
        from: call.from,
      };
    } catch (error) {
      console.error('Error making call:', error);
      throw new Error(`Failed to make call: ${error instanceof Error ? error.message : error}`);
    }
  }

  // Send SMS
  async sendSMS(to: string, message: string) {
    try {
      const sms = await this.client.messages.create({
        body: message,
        from: this.config.phoneNumber,
        to: to,
      });

      return {
        messageSid: sms.sid,
        status: sms.status,
        to: sms.to,
        from: sms.from,
      };
    } catch (error) {
      console.error('Error sending SMS:', error);
      throw new Error(`Failed to send SMS: ${error instanceof Error ? error.message : error}`);
    }
  }

  // Get call details
  async getCallDetails(callSid: string) {
    try {
      const call = await this.client.calls(callSid).fetch();

      return {
        sid: call.sid,
        status: call.status,
        duration: call.duration ? parseInt(call.duration) : 0,
        startTime: call.startTime,
        endTime: call.endTime,
        from: call.from,
        to: call.to,
        price: call.price,
        priceUnit: call.priceUnit,
      };
    } catch (error) {
      console.error('Error fetching call details:', error);
      throw new Error(`Failed to fetch call details: ${error instanceof Error ? error.message : error}`);
    }
  }

  // Get call recording
  async getCallRecording(callSid: string) {
    try {
      const recordings = await this.client.recordings.list({
        callSid: callSid,
        limit: 1,
      });

      if (recordings.length > 0) {
        const recording = recordings[0];
        return {
          recordingSid: recording.sid,
          duration: recording.duration,
          uri: recording.uri,
          mediaUrl: `https://api.twilio.com${recording.uri.replace('.json', '.mp3')}`,
        };
      }

      return null;
    } catch (error) {
      console.error('Error fetching recording:', error);
      throw new Error(`Failed to fetch recording: ${error instanceof Error ? error.message : error}`);
    }
  }

  // Purchase UK phone number
  async purchaseUKPhoneNumber() {
    try {
      // Search for available UK phone numbers
      const numbers = await this.client.availablePhoneNumbers('GB')
        .local
        .list({
          areaCode: 20, // London area code for professional appearance
          limit: 10
        });

      if (numbers.length === 0) {
        throw new Error('No UK phone numbers available');
      }

      // Purchase the first available number
      const selectedNumber = numbers[0];
      const purchasedNumber = await this.client.incomingPhoneNumbers.create({
        phoneNumber: selectedNumber.phoneNumber,
        voiceUrl: process.env.TWILIO_WEBHOOK_URL || 'https://your-app.com/api/twilio/voice',
        voiceMethod: 'POST',
        statusCallback: process.env.TWILIO_STATUS_CALLBACK_URL || 'https://your-app.com/api/twilio/status',
        statusCallbackMethod: 'POST',
      });

      return {
        phoneNumber: purchasedNumber.phoneNumber,
        sid: purchasedNumber.sid,
        friendlyName: purchasedNumber.friendlyName,
      };
    } catch (error) {
      console.error('Error purchasing phone number:', error);
      throw new Error(`Failed to purchase phone number: ${error instanceof Error ? error.message : error}`);
    }
  }

  // List owned phone numbers
  async listPhoneNumbers() {
    try {
      const numbers = await this.client.incomingPhoneNumbers.list();

      return numbers.map(number => ({
        sid: number.sid,
        phoneNumber: number.phoneNumber,
        friendlyName: number.friendlyName,
        voiceUrl: number.voiceUrl,
        capabilities: number.capabilities,
      }));
    } catch (error) {
      console.error('Error listing phone numbers:', error);
      throw new Error(`Failed to list phone numbers: ${error instanceof Error ? error.message : error}`);
    }
  }

  // Generate TwiML response for incoming calls
  generateTwiML(message: string = "Hello! Thank you for calling. Please hold while we connect you to our AI assistant.") {
    const twiml = new twilio.twiml.VoiceResponse();

    // Add a greeting message
    twiml.say({
      voice: 'alice',
      language: 'en-GB' // British English
    }, message);

    // Start recording the call
    twiml.record({
      timeout: 10,
      transcribe: true,
      transcribeCallback: '/api/twilio/transcribe',
      recordingStatusCallback: '/api/twilio/recording-status',
      maxLength: 3600, // 1 hour max
    });

    return twiml.toString();
  }

  // Generate simple TwiML response with just a message
  generateSimpleTwiML(message: string): string {
    const twiml = new twilio.twiml.VoiceResponse();
    twiml.say({
      voice: 'alice',
      language: 'en-GB'
    }, message);
    return twiml.toString();
  }

  // Generate TwiML for speech input processing
  generateSpeechResponseTwiML(message: string, continueListening: boolean = true): string {
    const twiml = new twilio.twiml.VoiceResponse();

    twiml.say({
      voice: 'alice',
      language: 'en-GB'
    }, message);

    if (continueListening) {
      twiml.gather({
        input: ['speech'],
        action: '/api/twilio/speech',
        method: 'POST',
        speechTimeout: 'auto',
        timeout: 5
      });
    }

    return twiml.toString();
  }

  // Validate webhook signature for security
  validateSignature(signature: string, url: string, params: any): boolean {
    const authToken = this.config.authToken;
    return twilio.validateRequest(authToken, signature, url, params);
  }

  // Static method to validate signature without instance
  static validateRequest(authToken: string, signature: string, url: string, params: any): boolean {
    return twilio.validateRequest(authToken, signature, url, params);
  }

  // Middleware function for signature validation
  static createSignatureValidationMiddleware(getAuthToken: (req: any) => string | null) {
    return (req: any, res: any, next: any) => {
      try {
        const twilioSignature = req.headers['x-twilio-signature'] as string;

        if (!twilioSignature) {
          console.error('Missing Twilio signature header');
          return res.status(401).json({ message: 'Missing Twilio signature' });
        }

        const authToken = getAuthToken(req);
        if (!authToken) {
          console.error('Unable to retrieve auth token for signature validation');
          return res.status(500).json({ message: 'Configuration error' });
        }

        const url = `${req.protocol}://${req.get('host')}${req.originalUrl}`;
        const isValid = TwilioService.validateRequest(authToken, twilioSignature, url, req.body);

        if (!isValid) {
          console.error('Invalid Twilio signature for URL:', url);
          return res.status(403).json({ message: 'Invalid signature' });
        }

        next();
      } catch (error) {
        console.error('Error validating Twilio signature:', error);
        res.status(500).json({ message: 'Signature validation error' });
      }
    };
  }
}

