import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Building, ArrowRight } from "lucide-react";
import type { InsertClinic } from "@shared/schema";

interface ClinicSetupProps {
  onComplete: () => void;
}

export default function ClinicSetup({ onComplete }: ClinicSetupProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [clinicData, setClinicData] = useState({
    name: "",
    phoneNumber: "",
    address: "",
  });

  const createClinicMutation = useMutation({
    mutationFn: async (data: Partial<InsertClinic>) => {
      return await apiRequest("POST", "/api/clinic", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clinic"] });
      toast({
        title: "Success",
        description: "Clinic created successfully! Welcome to ClinicVoice.",
      });
      onComplete();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to create clinic. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!clinicData.name.trim()) {
      toast({
        title: "Error",
        description: "Clinic name is required",
        variant: "destructive",
      });
      return;
    }

    createClinicMutation.mutate({
      name: clinicData.name,
      phoneNumber: clinicData.phoneNumber || null,
      address: clinicData.address || null,
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="w-full max-w-lg">
        <div className="text-center mb-8">
          <div className="flex justify-center mb-4">
            <div className="p-4 bg-primary/10 rounded-full">
              <Building className="h-8 w-8 text-primary" />
            </div>
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Welcome to ClinicVoice</h1>
          <p className="text-gray-600">Let's set up your clinic profile to get started</p>
        </div>

        <Card className="shadow-xl border-0">
          <CardHeader>
            <CardTitle className="text-center">Clinic Information</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <Label htmlFor="clinicName" className="text-sm font-medium">
                  Clinic Name *
                </Label>
                <Input
                  id="clinicName"
                  value={clinicData.name}
                  onChange={(e) => setClinicData({ ...clinicData, name: e.target.value })}
                  placeholder="Enter your clinic name"
                  className="mt-1"
                  required
                  data-testid="input-clinic-name"
                />
              </div>

              <div>
                <Label htmlFor="phoneNumber" className="text-sm font-medium">
                  Phone Number
                </Label>
                <Input
                  id="phoneNumber"
                  type="tel"
                  value={clinicData.phoneNumber}
                  onChange={(e) => setClinicData({ ...clinicData, phoneNumber: e.target.value })}
                  placeholder="+44 20 1234 5678"
                  className="mt-1"
                  data-testid="input-phone-number"
                />
              </div>

              <div>
                <Label htmlFor="address" className="text-sm font-medium">
                  Address
                </Label>
                <Textarea
                  id="address"
                  value={clinicData.address}
                  onChange={(e) => setClinicData({ ...clinicData, address: e.target.value })}
                  placeholder="Enter your clinic address"
                  rows={3}
                  className="mt-1"
                  data-testid="input-address"
                />
              </div>

              <Button
                type="submit"
                disabled={createClinicMutation.isPending}
                className="w-full bg-primary text-white hover:bg-primary/90"
                data-testid="button-create-clinic"
              >
                {createClinicMutation.isPending ? (
                  "Creating Clinic..."
                ) : (
                  <>
                    Create Clinic
                    <ArrowRight className="h-4 w-4 ml-2" />
                  </>
                )}
              </Button>
            </form>
          </CardContent>
        </Card>

        <p className="text-center text-sm text-gray-500 mt-6">
          You can update these details later in your settings.
        </p>
      </div>
    </div>
  );
}

