import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";
import Sidebar from "@/components/layout/sidebar";
import Header from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import TranscriptModal from "@/components/modals/transcript-modal";
import { Eye, Smile, Meh, Frown, Search } from "lucide-react";
import { format } from "date-fns";
import { isUnauthorizedError } from "@/lib/authUtils";
import type { CallLog } from "@shared/schema";

export default function CallLogs() {
  const { isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const [selectedCallLog, setSelectedCallLog] = useState<CallLog | null>(null);
  const [transcriptModalOpen, setTranscriptModalOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  const { data: callLogs = [], isLoading: logsLoading, error } = useQuery<CallLog[]>({
    queryKey: ["/api/call-logs"],
    retry: false,
  });

  // Handle unauthorized errors
  useEffect(() => {
    if (error && isUnauthorizedError(error as Error)) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [error, toast]);

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const getSentimentIcon = (score: number | null) => {
    if (score === null) return <Meh className="h-4 w-4 text-gray-400" />;
    if (score > 0.3) return <Smile className="h-4 w-4 text-success" />;
    if (score < -0.3) return <Frown className="h-4 w-4 text-error" />;
    return <Meh className="h-4 w-4 text-warning" />;
  };

  const getSentimentText = (score: number | null) => {
    if (score === null) return "Unknown";
    if (score > 0.3) return "Positive";
    if (score < -0.3) return "Negative";
    return "Neutral";
  };

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case "completed":
        return "bg-success/10 text-success";
      case "booked":
        return "bg-success/10 text-success";
      case "escalated":
        return "bg-error/10 text-error";
      default:
        return "bg-gray-100 text-gray-600";
    }
  };

  const formatDuration = (seconds: number | null) => {
    if (!seconds) return "0s";
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}m ${remainingSeconds}s`;
  };

  const handleViewTranscript = (callLog: CallLog) => {
    setSelectedCallLog(callLog);
    setTranscriptModalOpen(true);
  };

  // Filter call logs based on search term
  const filteredCallLogs = callLogs.filter(log =>
    log.callerPhone.toLowerCase().includes(searchTerm.toLowerCase()) ||
    (log.transcript && log.transcript.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  if (isLoading) {
    return (
      <div className="h-screen flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  if (!isAuthenticated) {
    return null; // Will redirect via useEffect
  }

  return (
    <div className="flex h-screen">
      <Sidebar />
      <div className="flex-1 flex flex-col overflow-hidden">
        <Header 
          title="Call Logs" 
          description="View and manage all incoming calls to your clinic."
        />

        <main className="flex-1 overflow-y-auto p-6">
          <Card className="border border-gray-100">
            <CardHeader className="border-b border-gray-100">
              <div className="flex items-center justify-between">
                <CardTitle>All Call Logs</CardTitle>
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                  <Input
                    placeholder="Search calls..."
                    className="pl-10 w-64"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>
              </div>
            </CardHeader>

            {logsLoading ? (
              <CardContent className="p-6">
                <div className="animate-pulse space-y-4">
                  {[...Array(10)].map((_, i) => (
                    <div key={i} className="h-12 bg-gray-200 rounded"></div>
                  ))}
                </div>
              </CardContent>
            ) : filteredCallLogs.length === 0 ? (
              <CardContent className="p-6">
                <div className="text-center py-8">
                  <p className="text-gray-500">
                    {searchTerm ? "No call logs match your search." : "No call logs found. Calls will appear here once received."}
                  </p>
                </div>
              </CardContent>
            ) : (
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Date & Time
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Caller
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Duration
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Status
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Sentiment
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Appointment
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Action
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {filteredCallLogs.map((log) => (
                      <tr key={log.id} className="hover:bg-gray-50 transition-colors">
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {log.createdAt ? format(new Date(log.createdAt), "MMM d, h:mm a") : "Unknown"}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-gray-900">
                            {log.callerPhone}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {formatDuration(log.duration)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <Badge className={getStatusColor(log.callStatus || "unknown")}>
                            {log.callStatus || "Unknown"}
                          </Badge>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="flex items-center space-x-2">
                            {getSentimentIcon(log.sentimentScore)}
                            <span className="text-sm text-gray-900">
                              {getSentimentText(log.sentimentScore)}
                            </span>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <Badge variant={log.appointmentBooked ? "default" : "secondary"}>
                            {log.appointmentBooked ? "Booked" : "No"}
                          </Badge>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleViewTranscript(log)}
                            className="text-primary hover:text-primary/80"
                          >
                            <Eye className="h-4 w-4" />
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </Card>
        </main>
      </div>

      <TranscriptModal
        open={transcriptModalOpen}
        onOpenChange={setTranscriptModalOpen}
        callLog={selectedCallLog}
      />
    </div>
  );
}

client/src/pages/dashboard.tsx.content:
import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useVoiceCommands } from "@/hooks/useVoiceCommands";
import { useQuery } from "@tanstack/react-query";
import Sidebar from "@/components/layout/sidebar";
import Header from "@/components/layout/header";
import StatsCards from "@/components/dashboard/stats-cards";
import RecentCallLogs from "@/components/dashboard/recent-call-logs";
import TodayAppointments from "@/components/dashboard/today-appointments";
import QuickActions from "@/components/dashboard/quick-actions";
import AIStatus from "@/components/dashboard/ai-status";
import LiveStatus from "@/components/dashboard/live-status";
import VoiceCommands from "@/components/voice/voice-commands";
import TranscriptModal from "@/components/modals/transcript-modal";
import ClinicSetup from "@/components/clinic-setup";
import { PageBackground } from "@/components/ui/page-background";
import { isUnauthorizedError } from "@/lib/authUtils";
import type { CallLog, Clinic } from "@shared/schema";

export default function Dashboard() {
  const { isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const [selectedCallLog, setSelectedCallLog] = useState<CallLog | null>(null);
  const [transcriptModalOpen, setTranscriptModalOpen] = useState(false);
  const [showClinicSetup, setShowClinicSetup] = useState(false);
  const [showVoiceCommands, setShowVoiceCommands] = useState(false);

  // Voice commands integration
  const { isListening, isSupported } = useVoiceCommands({
    enabled: true,
    onEmergency: () => {
      toast({
        title: "Emergency Protocol Activated",
        description: "Emergency alerts sent to staff",
        variant: "destructive"
      });
    },
    onTestAI: () => {
      toast({
        title: "AI Test Started",
        description: "Testing AI receptionist functionality",
      });
    }
  });

  // PERMANENTLY DISABLED - Use static clinic data to prevent infinite requests
  const clinic = null;
  const clinicLoading = false;

  // Handle authentication and clinic setup
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }

    // Show clinic setup if user is authenticated but doesn't have a clinic
    if (isAuthenticated && !clinicLoading && clinic === null) {
      setShowClinicSetup(true);
    } else if (clinic) {
      setShowClinicSetup(false);
    }
  }, [isAuthenticated, isLoading, clinic, clinicLoading, toast]);

  const handleViewTranscript = (callLog: CallLog) => {
    setSelectedCallLog(callLog);
    setTranscriptModalOpen(true);
  };

  if (isLoading || clinicLoading) {
    return (
      <div className="h-screen flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  if (!isAuthenticated) {
    return null; // Will redirect via useEffect
  }

  // Show clinic setup if no clinic exists
  if (showClinicSetup) {
    return <ClinicSetup onComplete={() => setShowClinicSetup(false)} />;
  }

  return (
    <div className="flex h-screen">
      <Sidebar />
      <div className="flex-1 flex flex-col overflow-hidden">
        <Header 
          title="Enhanced Dashboard" 
          description="Welcome back! Here's what's happening at your clinic today."
        />

        {/* Voice Commands Indicator */}
        {isSupported && (
          <div className="px-6 py-2 bg-blue-50 border-b">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <div className={`w-2 h-2 rounded-full ${isListening ? 'bg-red-500 animate-pulse' : 'bg-gray-400'}`}></div>
                <span className="text-sm text-blue-700">
                  Voice commands {isListening ? 'active' : 'available'} - Say "Emergency" for urgent help
                </span>
              </div>
              <button
                onClick={() => setShowVoiceCommands(!showVoiceCommands)}
                className="text-sm text-blue-600 hover:text-blue-800 underline"
                data-testid="toggle-voice-commands"
              >
                {showVoiceCommands ? 'Hide' : 'Show'} Voice Panel
              </button>
            </div>
          </div>
        )}

        <main className="flex-1 overflow-y-auto p-6">
          <StatsCards clinic={clinic} />

          {/* Voice Commands Panel */}
          {showVoiceCommands && isSupported && (
            <div className="mt-6">
              <VoiceCommands 
                onNavigate={(path) => window.location.href = path}
                onEmergency={() => {
                  toast({
                    title: "Emergency Alert Sent",
                    description: "All staff have been notified immediately",
                    variant: "destructive"
                  });
                }}
                onTestAI={() => {
                  toast({
                    title: "AI Test Call Started",
                    description: "Initiating test conversation with AI receptionist",
                  });
                }}
                onCheckStatus={() => {
                  toast({
                    title: "System Status Check",
                    description: "All systems operational - AI online, 12 calls today",
                  });
                }}
              />
            </div>
          )}

          {/* Enhanced Dashboard Layout */}
          <div className="grid grid-cols-1 xl:grid-cols-3 gap-6 mt-8">
            <div className="xl:col-span-2 space-y-6">
              <QuickActions />
              <LiveStatus />
            </div>
            <div className="space-y-6">
              <AIStatus />
            </div>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mt-8">
            <RecentCallLogs onViewTranscript={handleViewTranscript} clinic={clinic} />
            <TodayAppointments clinic={clinic} />
          </div>
        </main>
      </div>

      <TranscriptModal
        open={transcriptModalOpen}
        onOpenChange={setTranscriptModalOpen}
        callLog={selectedCallLog}
      />
    </div>
  );
}

