        <HelpCircle className={`${iconSizes[size]} transition-opacity group-hover:opacity-0`} />
        <Sparkles className={`${iconSizes[size]} absolute inset-0 opacity-0 transition-opacity group-hover:opacity-100 text-primary`} />
      </div>
    </Button>
  );
}

