                  <CardHeader className="pb-3">
                    <CardTitle className="text-lg">Next Steps</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-2 text-sm">
                    <p>â€¢ Test more scenarios in Simulations</p>
                    <p>â€¢ Configure business hours</p>
                    <p>â€¢ Set up appointment types</p>
                    <p>â€¢ Invite team members</p>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader className="pb-3">
                    <CardTitle className="text-lg">Need Help?</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-2 text-sm">
                    <p>â€¢ Watch tutorial videos</p>
                    <p>â€¢ Contact support team</p>
                    <p>â€¢ Join user community</p>
                    <p>â€¢ Schedule training call</p>
                  </CardContent>
                </Card>
              </div>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  const canProceed = () => {
    switch (currentStep) {
      case 1:
        return clinicProfile.name && clinicProfile.type && clinicProfile.phone;
      case 2:
        return true; // Optional fields
      case 3:
        return aiSettings.personality && aiSettings.voiceType && aiSettings.responseSpeed;
      case 4:
        return true; // Testing is optional
      case 5:
        return true;
      default:
        return false;
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6">
      <Card>
        <CardHeader>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-lg font-semibold">Setup Wizard</h1>
                <p className="text-sm text-gray-600">Step {currentStep} of {totalSteps}</p>
              </div>
              <Button
                variant="ghost"
                onClick={onSkip}
                className="text-gray-500"
                data-testid="skip-setup"
              >
                Skip Setup
              </Button>
            </div>
            <Progress value={progress} className="w-full" />
          </div>
        </CardHeader>

        <CardContent className="pt-6">
          {renderStep()}

          <div className="flex justify-between mt-8 pt-6 border-t">
            <Button
              variant="outline"
              onClick={handlePrevious}
              disabled={currentStep === 1}
              data-testid="previous-step"
            >
              <ArrowLeft className="h-4 w-4 mr-2" />
              Previous
            </Button>

            <Button
              onClick={handleNext}
              disabled={!canProceed()}
              data-testid="next-step"
            >
              {currentStep === totalSteps ? (
                <>
                  <CheckCircle className="h-4 w-4 mr-2" />
                  Complete Setup
                </>
              ) : (
                <>
                  Next
                  <ArrowRight className="h-4 w-4 ml-2" />
                </>
              )}
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/settings/natural-l.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { 
  MessageSquare, 
  Volume2, 
  Clock, 
  Heart, 
  Zap, 
  PlayCircle,
  Save,
  RotateCcw,
  Sparkles
} from "lucide-react";

interface ConfigSuggestion {
  id: string;
  prompt: string;
  description: string;
  category: 'personality' | 'speed' | 'voice' | 'behavior';
  icon: any;
}

interface VoicePreview {
  id: string;
  name: string;
  accent: string;
  gender: string;
  sample: string;
}

export default function NaturalLanguageConfig() {
  const [naturalInput, setNaturalInput] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [selectedVoice, setSelectedVoice] = useState("british-female-sarah");
  const [currentConfig, setCurrentConfig] = useState({
    personality: "Professional and empathetic",
    responseSpeed: "Moderate pace",
    voiceType: "British Female (Sarah)",
    specialInstructions: "Handle elderly patients with extra care and patience"
  });

  const suggestions: ConfigSuggestion[] = [
    {
      id: "friendly",
      prompt: "Make my AI more friendly and welcoming",
      description: "Increases warmth and empathy in responses",
      category: "personality",
      icon: Heart
    },
    {
      id: "faster",
      prompt: "Speed up responses for busy periods",
      description: "Reduces response time and conversation length",
      category: "speed",
      icon: Zap
    },
    {
      id: "professional",
      prompt: "Be more formal and professional",
      description: "Uses clinical terminology and formal language",
      category: "personality",
      icon: MessageSquare
    },
    {
      id: "elderly",
      prompt: "Speak slower for elderly patients",
      description: "Adjusts pace and volume for better comprehension",
      category: "behavior",
      icon: Clock
    },
    {
      id: "emergency",
      prompt: "Handle emergencies with priority",
      description: "Recognizes urgent situations and escalates quickly",
      category: "behavior",
      icon: Zap
    },
    {
      id: "voice-calm",
      prompt: "Use a calming, soothing voice",
      description: "Adjusts tone and pace for anxious patients",
      category: "voice",
      icon: Volume2
    }
  ];

  const voicePreviews: VoicePreview[] = [
    {
      id: "british-female-sarah",
      name: "Sarah",
      accent: "British",
      gender: "Female",
      sample: "Good morning, this is Sarah from the clinic. How may I help you today?"
    },
    {
      id: "british-male-james",
      name: "James",
      accent: "British",
      gender: "Male",
      sample: "Hello, this is James. I'd be happy to help you schedule an appointment."
    },
    {
      id: "american-female-emma",
      name: "Emma",
      accent: "American",
      gender: "Female",
      sample: "Hi there! This is Emma from your healthcare provider. What can I do for you?"
    },
    {
      id: "american-male-david",
      name: "David",
      accent: "American",
      gender: "Male",
      sample: "Good afternoon, this is David. I'm here to assist with your healthcare needs."
    }
  ];

  const handleNaturalInput = async () => {
    if (!naturalInput.trim()) return;

    setIsProcessing(true);

    // Simulate AI processing
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Mock configuration update based on input
    const input = naturalInput.toLowerCase();
    let newConfig = { ...currentConfig };

    if (input.includes('friendly') || input.includes('warm')) {
      newConfig.personality = "Warm and friendly";
    }
    if (input.includes('professional') || input.includes('formal')) {
      newConfig.personality = "Professional and formal";
    }
    if (input.includes('fast') || input.includes('quick')) {
      newConfig.responseSpeed = "Fast and efficient";
    }
    if (input.includes('slow') || input.includes('patient')) {
      newConfig.responseSpeed = "Slow and patient";
    }
    if (input.includes('elderly') || input.includes('older')) {
      newConfig.specialInstructions = "Extra patience for elderly patients, speak clearly and slowly";
    }
    if (input.includes('emergency') || input.includes('urgent')) {
      newConfig.specialInstructions = "Prioritize emergency situations and escalate immediately";
    }

    setCurrentConfig(newConfig);
    setIsProcessing(false);
    setNaturalInput("");
  };

  const applySuggestion = (suggestion: ConfigSuggestion) => {
    setNaturalInput(suggestion.prompt);
  };

  const playVoicePreview = (voiceId: string) => {
    const voice = voicePreviews.find(v => v.id === voiceId);
    if (voice) {
      // In a real implementation, this would play the actual voice sample
      console.log(`Playing voice preview: ${voice.sample}`);
    }
  };

  return (
    <div className="space-y-6">
      {/* Natural Language Input */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Sparkles className="h-5 w-5 mr-2" />
            Configure AI with Natural Language
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div>
            <Label htmlFor="natural-input">Tell your AI how to behave</Label>
            <div className="flex space-x-2 mt-2">
              <Textarea
                id="natural-input"
                value={naturalInput}
                onChange={(e) => setNaturalInput(e.target.value)}
                placeholder="e.g., 'Make my AI more friendly and patient with elderly callers'"
                className="flex-1"
                rows={3}
                data-testid="natural-language-input"
              />
              <Button
                onClick={handleNaturalInput}
                disabled={isProcessing || !naturalInput.trim()}
                className="px-6"
                data-testid="apply-config-button"
              >
                {isProcessing ? (
                  <>
                    <RotateCcw className="h-4 w-4 mr-2 animate-spin" />
                    Applying...
                  </>
                ) : (
                  <>
                    <Sparkles className="h-4 w-4 mr-2" />
                    Apply
                  </>
                )}
              </Button>
            </div>
          </div>

          {/* Quick Suggestions */}
          <div>
            <Label>Quick Suggestions</Label>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3 mt-2">
              {suggestions.map((suggestion) => {
                const Icon = suggestion.icon;
                return (
                  <Button
                    key={suggestion.id}
                    variant="outline"
                    className="h-auto p-3 justify-start"
                    onClick={() => applySuggestion(suggestion)}
                    data-testid={`suggestion-${suggestion.id}`}
                  >
                    <Icon className="h-4 w-4 mr-3" />
                    <div className="text-left">
                      <p className="text-sm font-medium">{suggestion.prompt}</p>
                      <p className="text-xs text-gray-600">{suggestion.description}</p>
                    </div>
                  </Button>
                );
              })}
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Current Configuration */}
      <Card>
        <CardHeader>
          <CardTitle>Current AI Configuration</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label className="text-sm font-medium text-gray-600">Personality</Label>
              <p className="text-lg">{currentConfig.personality}</p>
            </div>
            <div>
              <Label className="text-sm font-medium text-gray-600">Response Speed</Label>
              <p className="text-lg">{currentConfig.responseSpeed}</p>
            </div>
            <div>
              <Label className="text-sm font-medium text-gray-600">Voice Type</Label>
              <p className="text-lg">{currentConfig.voiceType}</p>
            </div>
            <div>
              <Label className="text-sm font-medium text-gray-600">Special Instructions</Label>
              <p className="text-lg">{currentConfig.specialInstructions}</p>
            </div>
          </div>

          <div className="flex space-x-3 pt-4 border-t">
            <Button data-testid="save-config-button">
              <Save className="h-4 w-4 mr-2" />
              Save Configuration
            </Button>
            <Button variant="outline" data-testid="test-config-button">
              <PlayCircle className="h-4 w-4 mr-2" />
              Test Configuration
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Voice Selection with Previews */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Volume2 className="h-5 w-5 mr-2" />
            Voice Selection & Preview
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {voicePreviews.map((voice) => (
              <div
                key={voice.id}
                className={`p-4 border rounded-lg cursor-pointer transition-colors ${
                  selectedVoice === voice.id
                    ? 'border-primary bg-primary/10'
                    : 'border-gray-200 hover:border-gray-300'
                }`}
                onClick={() => setSelectedVoice(voice.id)}
                data-testid={`voice-option-${voice.id}`}
              >
                <div className="flex items-center justify-between mb-2">
                  <div>
                    <p className="font-medium">{voice.name}</p>
                    <div className="flex space-x-2">
                      <Badge variant="secondary">{voice.accent}</Badge>
                      <Badge variant="outline">{voice.gender}</Badge>
                    </div>
                  </div>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={(e) => {
                      e.stopPropagation();
                      playVoicePreview(voice.id);
                    }}
                    data-testid={`play-voice-${voice.id}`}
                  >
                    <PlayCircle className="h-4 w-4" />
                  </Button>
                </div>
                <p className="text-sm text-gray-600 italic">"{voice.sample}"</p>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* AI Behavior Examples */}
      <Card>
        <CardHeader>
          <CardTitle>Example Conversations</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="border rounded-lg p-4">
              <h4 className="font-medium mb-3">Appointment Booking</h4>
              <div className="space-y-2 text-sm">
                <div className="flex">
                  <Badge variant="secondary" className="mr-3">Caller</Badge>
                  <p>"I need to book an appointment for next week."</p>
                </div>
                <div className="flex">
                  <Badge variant="default" className="mr-3">AI</Badge>
                  <p className="italic">"I'd be happy to help you schedule an appointment. May I have your name and preferred day?"</p>
                </div>
              </div>
            </div>

            <div className="border rounded-lg p-4">
              <h4 className="font-medium mb-3">Emergency Situation</h4>
              <div className="space-y-2 text-sm">
                <div className="flex">
                  <Badge variant="secondary" className="mr-3">Caller</Badge>
                  <p>"I'm having chest pains right now!"</p>
                </div>
                <div className="flex">
                  <Badge variant="destructive" className="mr-3">AI</Badge>
                  <p className="italic">"This sounds urgent. I'm immediately connecting you to our emergency line. Please hold while I get you help right away."</p>
                </div>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/simulation/appoint.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Calendar, Clock, User, Phone, MessageCircle, CheckCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface AppointmentBookingDemoProps {
  clinic?: any;
}

export default function AppointmentBookingDemo({ clinic }: AppointmentBookingDemoProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [step, setStep] = useState<'form' | 'confirmation' | 'complete'>('form');
  const [formData, setFormData] = useState({
    patientName: '',
    phoneNumber: '',
    email: '',
    appointmentType: '',
    preferredDate: '',
    preferredTime: '',
    notes: ''
  });

  const createAppointmentMutation = useMutation({
    mutationFn: (data: any) => apiRequest('/api/appointments', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/appointments'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
      setStep('complete');
      toast({
        title: "Appointment Booked Successfully",
        description: "The patient will receive a confirmation message shortly.",
      });
    },
    onError: () => {
      toast({
        title: "Booking Failed",
        description: "Please try again or contact support.",
        variant: "destructive",
      });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!clinic) {
      toast({
        title: "No Clinic Setup",
        description: "Please set up your clinic first to book appointments.",
        variant: "destructive",
      });
      return;
    }

    setStep('confirmation');
  };

  const confirmBooking = () => {
    const appointmentData = {
      clinicId: clinic.id,
      patientName: formData.patientName,
      phoneNumber: formData.phoneNumber,
      email: formData.email,
      appointmentType: formData.appointmentType,
      appointmentDate: new Date(`${formData.preferredDate} ${formData.preferredTime}`).toISOString(),
      status: 'scheduled',
      notes: formData.notes,
      source: 'ai_receptionist',
      duration: 30, // 30 minutes default
    };

    createAppointmentMutation.mutate(appointmentData);
  };

  const resetDemo = () => {
    setStep('form');
    setFormData({
      patientName: '',
      phoneNumber: '',
      email: '',
      appointmentType: '',
      preferredDate: '',
      preferredTime: '',
      notes: ''
    });
  };

  const generateSampleData = () => {
    const sampleNames = ['John Smith', 'Sarah Johnson', 'Michael Brown', 'Emma Wilson', 'David Lee'];
    const sampleTypes = ['General Consultation', 'Follow-up', 'Annual Check-up', 'Urgent Care', 'Specialist Referral'];
    const name = sampleNames[Math.floor(Math.random() * sampleNames.length)];
    const type = sampleTypes[Math.floor(Math.random() * sampleTypes.length)];

    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);

    setFormData({
      patientName: name,
      phoneNumber: '+44 7700 900' + Math.floor(Math.random() * 1000).toString().padStart(3, '0'),
      email: name.toLowerCase().replace(' ', '.') + '@email.com',
      appointmentType: type,
      preferredDate: tomorrow.toISOString().split('T')[0],
      preferredTime: '14:30',
      notes: 'Patient called requesting appointment. Mentioned some minor concerns to discuss.'
    });
  };

  if (step === 'complete') {
    return (
      <Card className="border-green-200 bg-green-50">
        <CardHeader className="text-center">
          <div className="flex justify-center mb-4">
            <CheckCircle className="h-16 w-16 text-green-600" />
          </div>
          <CardTitle className="text-green-800">Appointment Booked Successfully!</CardTitle>
        </CardHeader>
        <CardContent className="text-center space-y-4">
          <div className="bg-white p-4 rounded-lg">
            <h4 className="font-semibold mb-2">Booking Details</h4>
            <div className="text-sm space-y-1">
              <p><strong>Patient:</strong> {formData.patientName}</p>
              <p><strong>Type:</strong> {formData.appointmentType}</p>
              <p><strong>Date & Time:</strong> {formData.preferredDate} at {formData.preferredTime}</p>
              <p><strong>Phone:</strong> {formData.phoneNumber}</p>
            </div>
          </div>
          <Button onClick={resetDemo} className="w-full">
            Book Another Appointment
          </Button>
        </CardContent>
      </Card>
    );
  }

  if (step === 'confirmation') {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <MessageCircle className="h-5 w-5 mr-2" />
            AI Receptionist - Confirm Booking
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="bg-blue-50 p-4 rounded-lg border">
            <h4 className="font-semibold mb-3 flex items-center">
              <Phone className="h-4 w-4 mr-2" />
              AI Receptionist Summary
            </h4>
            <div className="text-sm space-y-2">
              <p>"Thank you, {formData.patientName}. I have the following details for your appointment:"</p>
              <div className="bg-white p-3 rounded border-l-4 border-blue-500">
                <ul className="space-y-1">
                  <li><strong>Patient Name:</strong> {formData.patientName}</li>
                  <li><strong>Phone Number:</strong> {formData.phoneNumber}</li>
                  <li><strong>Appointment Type:</strong> {formData.appointmentType}</li>
                  <li><strong>Preferred Date:</strong> {formData.preferredDate}</li>
                  <li><strong>Preferred Time:</strong> {formData.preferredTime}</li>
                  {formData.notes && <li><strong>Notes:</strong> {formData.notes}</li>}
                </ul>
              </div>
              <p>"Shall I go ahead and book this appointment for you?"</p>
            </div>
          </div>

          <div className="flex space-x-3">
            <Button onClick={confirmBooking} className="flex-1" disabled={createAppointmentMutation.isPending}>
              {createAppointmentMutation.isPending ? "Booking..." : "Yes, Confirm Booking"}
            </Button>
            <Button variant="outline" onClick={() => setStep('form')} className="flex-1">
              No, Go Back
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <span className="flex items-center">
            <Calendar className="h-5 w-5 mr-2" />
            Appointment Booking Simulation
          </span>
          <Button variant="outline" size="sm" onClick={generateSampleData}>
            Generate Sample Data
          </Button>
        </CardTitle>
        <p className="text-sm text-gray-600">
          Simulate how your AI receptionist handles appointment bookings
        </p>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
