}

client/src/components/simulation/call-an.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { 
  BarChart3, 
  TrendingUp, 
  Clock, 
  Phone, 
  Calendar,
  MessageCircle,
  CheckCircle,
  AlertTriangle,
  Users,
  Star
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface CallAnalyticsDemoProps {
  clinic?: any;
}

export default function CallAnalyticsDemo({ clinic }: CallAnalyticsDemoProps) {
  const { toast } = useToast();
  const [isGenerating, setIsGenerating] = useState(false);
  const [analytics, setAnalytics] = useState({
    totalCalls: 0,
    appointmentsBooked: 0,
    averageCallDuration: 0,
    customerSatisfaction: 0,
    callResolutionRate: 0,
    busyHours: [] as Array<{ hour: string; calls: number }>,
    commonReasons: [] as Array<{ reason: string; count: number; percentage: number }>,
    sentimentAnalysis: {
      positive: 0,
      neutral: 0,
      negative: 0
    }
  });

  const generateAnalytics = async () => {
    if (!clinic) {
      toast({
        title: "No Clinic Setup",
        description: "Please set up your clinic first to generate analytics.",
        variant: "destructive",
      });
      return;
    }

    setIsGenerating(true);

    // Simulate analytics generation
    for (let i = 0; i <= 100; i += 10) {
      await new Promise(resolve => setTimeout(resolve, 200));
      setAnalytics(prev => ({
        ...prev,
        totalCalls: Math.floor((i / 100) * 247),
        appointmentsBooked: Math.floor((i / 100) * 183),
        averageCallDuration: Math.floor((i / 100) * 240), // seconds
        customerSatisfaction: (i / 100) * 4.7,
        callResolutionRate: (i / 100) * 94.2,
      }));
    }

    // Set final detailed analytics
    setAnalytics({
      totalCalls: 247,
      appointmentsBooked: 183,
      averageCallDuration: 240,
      customerSatisfaction: 4.7,
      callResolutionRate: 94.2,
      busyHours: [
        { hour: '9:00 AM', calls: 23 },
        { hour: '10:00 AM', calls: 31 },
        { hour: '2:00 PM', calls: 28 },
        { hour: '3:00 PM', calls: 35 },
        { hour: '4:00 PM', calls: 22 }
      ],
      commonReasons: [
        { reason: 'Appointment booking', count: 156, percentage: 63 },
        { reason: 'Prescription inquiries', count: 38, percentage: 15 },
        { reason: 'Schedule changes', count: 31, percentage: 13 },
        { reason: 'General information', count: 22, percentage: 9 }
      ],
      sentimentAnalysis: {
        positive: 78,
        neutral: 18,
        negative: 4
      }
    });

    setIsGenerating(false);
    toast({
      title: "Analytics Generated",
      description: "Call analytics have been generated successfully.",
    });
  };

  const resetAnalytics = () => {
    setAnalytics({
      totalCalls: 0,
      appointmentsBooked: 0,
      averageCallDuration: 0,
      customerSatisfaction: 0,
      callResolutionRate: 0,
      busyHours: [] as Array<{ hour: string; calls: number }>,
      commonReasons: [] as Array<{ reason: string; count: number; percentage: number }>,
      sentimentAnalysis: { positive: 0, neutral: 0, negative: 0 }
    });
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <span className="flex items-center">
              <BarChart3 className="h-5 w-5 mr-2" />
              AI Call Analytics Demo
            </span>
            <Button 
              onClick={isGenerating ? () => {} : (analytics.totalCalls > 0 ? resetAnalytics : generateAnalytics)}
              disabled={isGenerating}
              variant={analytics.totalCalls > 0 ? "outline" : "default"}
            >
              {isGenerating ? "Generating..." : analytics.totalCalls > 0 ? "Reset Demo" : "Generate Analytics"}
            </Button>
          </CardTitle>
          <p className="text-sm text-gray-600">
            See comprehensive analytics of your AI receptionist performance
          </p>
        </CardHeader>
        <CardContent>
          {analytics.totalCalls === 0 && !isGenerating ? (
            <div className="text-center py-8">
              <BarChart3 className="h-16 w-16 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-semibold mb-2">Generate Sample Analytics</h3>
              <p className="text-gray-600 mb-4">
                See how your AI receptionist analytics would look with sample data
              </p>
            </div>
          ) : (
            <div className="space-y-6">
              {/* Key Metrics */}
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <div className="flex items-center justify-between">
                    <Phone className="h-8 w-8 text-blue-600" />
                    <span className="text-2xl font-bold text-blue-900">
                      {analytics.totalCalls}
                    </span>
                  </div>
                  <p className="text-sm text-blue-700 mt-2">Total Calls</p>
                </div>

                <div className="bg-green-50 p-4 rounded-lg">
                  <div className="flex items-center justify-between">
                    <Calendar className="h-8 w-8 text-green-600" />
                    <span className="text-2xl font-bold text-green-900">
                      {analytics.appointmentsBooked}
                    </span>
                  </div>
                  <p className="text-sm text-green-700 mt-2">Appointments Booked</p>
                </div>

                <div className="bg-purple-50 p-4 rounded-lg">
                  <div className="flex items-center justify-between">
                    <Clock className="h-8 w-8 text-purple-600" />
                    <span className="text-2xl font-bold text-purple-900">
                      {Math.floor(analytics.averageCallDuration / 60)}:{(analytics.averageCallDuration % 60).toString().padStart(2, '0')}
                    </span>
                  </div>
                  <p className="text-sm text-purple-700 mt-2">Avg Duration</p>
                </div>

                <div className="bg-orange-50 p-4 rounded-lg">
                  <div className="flex items-center justify-between">
                    <Star className="h-8 w-8 text-orange-600" />
                    <span className="text-2xl font-bold text-orange-900">
                      {analytics.customerSatisfaction.toFixed(1)}
                    </span>
                  </div>
                  <p className="text-sm text-orange-700 mt-2">Satisfaction</p>
                </div>
              </div>

              {/* Resolution Rate */}
              <Card>
                <CardHeader className="pb-3">
                  <CardTitle className="text-lg flex items-center">
                    <CheckCircle className="h-5 w-5 mr-2 text-green-600" />
                    Call Resolution Rate
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm text-gray-600">Calls resolved successfully</span>
                    <span className="font-semibold">{analytics.callResolutionRate.toFixed(1)}%</span>
                  </div>
                  <Progress value={analytics.callResolutionRate} className="h-2" />
                </CardContent>
              </Card>

              {/* Busy Hours */}
              {analytics.busyHours.length > 0 && (
                <Card>
                  <CardHeader className="pb-3">
                    <CardTitle className="text-lg flex items-center">
                      <TrendingUp className="h-5 w-5 mr-2 text-blue-600" />
                      Busiest Hours
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      {analytics.busyHours.map((hour, index) => (
                        <div key={index} className="flex items-center justify-between">
                          <span className="text-sm font-medium">{hour.hour}</span>
                          <div className="flex items-center space-x-3 flex-1 mx-4">
                            <Progress value={(hour.calls / 35) * 100} className="h-2 flex-1" />
                            <span className="text-sm text-gray-600 min-w-[30px]">{hour.calls}</span>
                          </div>
                        </div>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              )}

              {/* Common Call Reasons */}
              {analytics.commonReasons.length > 0 && (
                <Card>
                  <CardHeader className="pb-3">
                    <CardTitle className="text-lg flex items-center">
                      <MessageCircle className="h-5 w-5 mr-2 text-purple-600" />
                      Common Call Reasons
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      {analytics.commonReasons.map((reason, index) => (
                        <div key={index} className="flex items-center justify-between">
                          <span className="text-sm font-medium">{reason.reason}</span>
                          <div className="flex items-center space-x-3">
                            <Badge variant="outline">{reason.percentage}%</Badge>
                            <span className="text-sm text-gray-600 min-w-[30px]">{reason.count}</span>
                          </div>
                        </div>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              )}

              {/* Sentiment Analysis */}
              {analytics.sentimentAnalysis.positive > 0 && (
                <Card>
                  <CardHeader className="pb-3">
                    <CardTitle className="text-lg flex items-center">
                      <Users className="h-5 w-5 mr-2 text-green-600" />
                      Patient Sentiment Analysis
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      <div className="flex items-center justify-between">
                        <span className="text-sm font-medium flex items-center">
                          <div className="w-3 h-3 bg-green-500 rounded-full mr-2"></div>
                          Positive
                        </span>
                        <div className="flex items-center space-x-3 flex-1 mx-4">
                          <Progress value={analytics.sentimentAnalysis.positive} className="h-2 flex-1" />
                          <span className="text-sm text-gray-600 min-w-[30px]">{analytics.sentimentAnalysis.positive}%</span>
                        </div>
                      </div>

                      <div className="flex items-center justify-between">
                        <span className="text-sm font-medium flex items-center">
                          <div className="w-3 h-3 bg-yellow-500 rounded-full mr-2"></div>
                          Neutral
                        </span>
                        <div className="flex items-center space-x-3 flex-1 mx-4">
                          <Progress value={analytics.sentimentAnalysis.neutral} className="h-2 flex-1" />
                          <span className="text-sm text-gray-600 min-w-[30px]">{analytics.sentimentAnalysis.neutral}%</span>
                        </div>
                      </div>

                      <div className="flex items-center justify-between">
                        <span className="text-sm font-medium flex items-center">
                          <div className="w-3 h-3 bg-red-500 rounded-full mr-2"></div>
                          Negative
                        </span>
                        <div className="flex items-center space-x-3 flex-1 mx-4">
                          <Progress value={analytics.sentimentAnalysis.negative} className="h-2 flex-1" />
                          <span className="text-sm text-gray-600 min-w-[30px]">{analytics.sentimentAnalysis.negative}%</span>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              )}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/simulation/call-lo.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Phone, MessageCircle, CheckCircle, Clock, User } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface CallLogDemoProps {
  clinic?: any;
}

export default function CallLogDemo({ clinic }: CallLogDemoProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isSimulating, setIsSimulating] = useState(false);
  const [currentStep, setCurrentStep] = useState(0);
  const [callData, setCallData] = useState({
    patientName: "Sarah Thompson",
    phoneNumber: "+44 7700 900456",
    callDuration: 0,
    appointmentBooked: false,
    summary: "",
    transcript: "",
  });

  const createCallLogMutation = useMutation({
    mutationFn: (data: any) => apiRequest('/api/call-logs', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/call-logs'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
      toast({
        title: "Call Log Created",
        description: "AI receptionist call has been logged successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Failed to Log Call",
        description: "Please try again or contact support.",
        variant: "destructive",
      });
    }
  });

  const simulationSteps = [
    {
      title: "Incoming Call",
      description: "Patient calls the clinic",
      content: "ðŸ“ž Incoming call from +44 7700 900456",
      duration: 1000,
    },
    {
      title: "AI Greeting",
      description: "AI receptionist answers",
      content: "ðŸ¤– 'Good afternoon, thank you for calling [Clinic Name]. This is your AI receptionist. How may I help you today?'",
      duration: 2000,
    },
    {
      title: "Patient Request",
      description: "Patient states their needs",
      content: "ðŸ‘¤ 'Hi, I'd like to book an appointment with Dr. Smith for next week. I've been having some headaches.'",
      duration: 2000,
    },
    {
      title: "AI Processing",
      description: "AI gathers information",
      content: "ðŸ¤– 'I can help you with that. Let me check Dr. Smith's availability. May I have your name and date of birth for verification?'",
      duration: 2000,
    },
    {
      title: "Appointment Booking",
      description: "AI books the appointment",
      content: "ðŸ¤– 'Perfect! I have an appointment available on Tuesday at 2:30 PM. Would that work for you, Sarah?'",
      duration: 2000,
    },
    {
      title: "Confirmation",
      description: "AI confirms details",
      content: "ðŸ¤– 'Excellent! Your appointment is confirmed for Tuesday, [Date] at 2:30 PM with Dr. Smith. You'll receive a confirmation SMS shortly. Is there anything else I can help you with today?'",
      duration: 2000,
    },
    {
      title: "Call Complete",
      description: "Call ends successfully",
      content: "âœ… Call completed - Appointment booked successfully",
      duration: 1000,
    },
  ];

  const startSimulation = async () => {
    if (!clinic) {
      toast({
        title: "No Clinic Setup",
        description: "Please set up your clinic first to simulate calls.",
        variant: "destructive",
      });
      return;
    }

    setIsSimulating(true);
    setCurrentStep(0);
    setCallData({
      ...callData,
      callDuration: 0,
      appointmentBooked: false,
      summary: "",
      transcript: "",
    });

    // Simulate call progression
    for (let i = 0; i < simulationSteps.length; i++) {
      setCurrentStep(i);
      await new Promise(resolve => setTimeout(resolve, simulationSteps[i].duration));

      // Update call duration
      setCallData(prev => ({
        ...prev,
        callDuration: prev.callDuration + simulationSteps[i].duration / 1000,
      }));
    }

    // Mark appointment as booked
    setCallData(prev => ({
      ...prev,
      appointmentBooked: true,
      summary: "Patient called to book appointment with Dr. Smith for headaches. Appointment successfully scheduled for Tuesday at 2:30 PM. Patient was polite and satisfied with service.",
      transcript: simulationSteps.map((step, index) => 
        `[${new Date(Date.now() - (simulationSteps.length - index) * 1000).toLocaleTimeString()}] ${step.content.replace(/ðŸ¤–|ðŸ‘¤|ðŸ“ž|âœ…/g, '').trim()}`
      ).join('\n'),
    }));

    // Create the call log
    const callLogData = {
      clinicId: clinic.id,
      phoneNumber: callData.phoneNumber,
      callerName: callData.patientName,
      duration: Math.round(simulationSteps.reduce((acc, step) => acc + step.duration, 0) / 1000),
      summary: "Patient called to book appointment with Dr. Smith for headaches. Appointment successfully scheduled for Tuesday at 2:30 PM.",
      transcript: simulationSteps.map(step => step.content).join('\n'),
      appointmentBooked: true,
      sentiment: 0.8, // Positive sentiment
      status: 'completed',
      aiResponse: true,
    };

    createCallLogMutation.mutate(callLogData);
    setIsSimulating(false);
  };

  const resetSimulation = () => {
    setCurrentStep(0);
    setIsSimulating(false);
    setCallData({
      patientName: "Sarah Thompson",
      phoneNumber: "+44 7700 900456",
      callDuration: 0,
      appointmentBooked: false,
      summary: "",
      transcript: "",
    });
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center">
          <Phone className="h-5 w-5 mr-2" />
          AI Receptionist Call Simulation
        </CardTitle>
        <p className="text-sm text-gray-600">
          Watch how your AI receptionist handles patient calls
        </p>
      </CardHeader>
      <CardContent className="space-y-6">
        {!isSimulating && currentStep === 0 ? (
          <div className="text-center space-y-4">
            <div className="p-6 bg-blue-50 rounded-lg">
              <Phone className="h-12 w-12 mx-auto text-blue-600 mb-4" />
              <h3 className="font-semibold mb-2">Ready to Simulate a Call</h3>
              <p className="text-sm text-gray-600 mb-4">
                This simulation shows how your AI receptionist handles incoming patient calls, 
                including appointment booking and information gathering.
              </p>
              <Button onClick={startSimulation} disabled={!clinic || createCallLogMutation.isPending}>
                {!clinic ? "Setup Clinic First" : "Start Call Simulation"}
              </Button>
            </div>
          </div>
        ) : (
          <div className="space-y-4">
            {/* Progress indicator */}
            <div className="flex items-center justify-between text-sm text-gray-600">
              <span>Step {currentStep + 1} of {simulationSteps.length}</span>
              <span>{Math.round(callData.callDuration)}s</span>
            </div>

            {/* Progress bar */}
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div 
                className="bg-blue-600 h-2 rounded-full transition-all duration-1000"
                style={{ width: `${((currentStep + 1) / simulationSteps.length) * 100}%` }}
              />
            </div>

            {/* Current step display */}
            <div className="bg-white border rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <h4 className="font-semibold">{simulationSteps[currentStep]?.title}</h4>
                <Badge variant="outline">
                  {isSimulating ? "In Progress" : "Completed"}
                </Badge>
              </div>
              <p className="text-sm text-gray-600 mb-3">{simulationSteps[currentStep]?.description}</p>
              <div className="bg-gray-50 rounded p-3 font-mono text-sm">
                {simulationSteps[currentStep]?.content}
              </div>
            </div>

            {/* Call summary after completion */}
            {!isSimulating && currentStep >= simulationSteps.length - 1 && (
              <div className="bg-green-50 border border-green-200 rounded-lg p-4">
                <div className="flex items-center mb-3">
                  <CheckCircle className="h-5 w-5 text-green-600 mr-2" />
                  <h4 className="font-semibold text-green-800">Call Completed Successfully</h4>
                </div>
                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <span className="font-medium">Patient:</span> {callData.patientName}
                  </div>
                  <div>
                    <span className="font-medium">Duration:</span> {Math.round(callData.callDuration)}s
                  </div>
                  <div>
                    <span className="font-medium">Phone:</span> {callData.phoneNumber}
                  </div>
                  <div>
                    <span className="font-medium">Appointment:</span> 
                    <Badge className="ml-2" variant={callData.appointmentBooked ? "default" : "secondary"}>
                      {callData.appointmentBooked ? "Booked" : "Not Booked"}
                    </Badge>
                  </div>
                </div>
                <Button onClick={resetSimulation} className="w-full mt-4" variant="outline">
                  Run Another Simulation
                </Button>
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

