import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Progress } from "@/components/ui/progress";
import { 
  AlertTriangle, 
  Phone, 
  Clock, 
  Shield, 
  CheckCircle,
  Heart,
  MessageCircle,
  PhoneCall,
  Zap
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface EmergencyProtocolDemoProps {
  clinic?: any;
}

interface EmergencyScenario {
  id: string;
  title: string;
  description: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  protocol: string[];
  icon: any;
  color: string;
}

export default function EmergencyProtocolDemo({ clinic }: EmergencyProtocolDemoProps) {
  const { toast } = useToast();
  const [selectedScenario, setSelectedScenario] = useState<string>("");
  const [isActive, setIsActive] = useState(false);
  const [currentStep, setCurrentStep] = useState(0);
  const [protocolComplete, setProtocolComplete] = useState(false);

  const emergencyScenarios: EmergencyScenario[] = [
    {
      id: "chest-pain",
      title: "Chest Pain Emergency",
      description: "Patient reporting severe chest pain and difficulty breathing",
      severity: "critical",
      protocol: [
        "Immediate triage: CRITICAL priority",
        "Advise patient to call 999 immediately",
        "Do not hang up until emergency services contacted",
        "Instruct patient to chew aspirin if available and not allergic",
        "Keep patient calm and monitor until help arrives",
        "Log emergency call with timestamp",
        "Alert on-call physician immediately"
      ],
      icon: Heart,
      color: "red"
    },
    {
      id: "allergic-reaction",
      title: "Severe Allergic Reaction",
      description: "Patient experiencing swelling, difficulty breathing after medication",
      severity: "critical",
      protocol: [
        "Immediate assessment: CRITICAL emergency",
        "Direct patient to call 999 immediately",
        "Ask about EpiPen availability",
        "Instruct use of EpiPen if available",
        "Keep airway clear, stay on line",
        "Document allergen and reaction details",
        "Emergency protocol notification sent"
      ],
      icon: AlertTriangle,
      color: "red"
    },
    {
      id: "fall-injury",
      title: "Serious Fall with Injury",
      description: "Patient fell and cannot move, possible head or spinal injury",
      severity: "high",
      protocol: [
        "Assess consciousness and mobility",
        "Advise NOT to move if spinal injury suspected",
        "Direct to call 999 for ambulance",
        "Monitor patient consciousness level",
        "Document circumstances of fall",
        "Schedule urgent follow-up if stable",
        "Complete incident report"
      ],
      icon: AlertTriangle,
      color: "orange"
    },
    {
      id: "mental-health",
      title: "Mental Health Crisis",
      description: "Patient expressing suicidal thoughts or severe distress",
      severity: "high",
      protocol: [
        "Immediate mental health protocol activated",
        "Keep patient talking, show empathy",
        "Assess immediate danger to self/others",
        "Connect to crisis helpline: 116 123",
        "Do not leave patient alone",
        "Arrange emergency mental health referral",
        "Follow up within 24 hours mandatory"
      ],
      icon: Heart,
      color: "purple"
    },
    {
      id: "medication-error",
      title: "Medication Overdose",
      description: "Patient accidentally took too much medication",
      severity: "medium",
      protocol: [
        "Identify medication and dosage taken",
        "Contact NHS 111 for immediate advice",
        "Do not induce vomiting unless instructed",
        "Keep medication packaging available",
        "Monitor patient symptoms closely",
        "Document full medication history",
        "Arrange urgent consultation if needed"
      ],
      icon: Zap,
      color: "yellow"
    }
  ];

  const startProtocol = async () => {
    if (!clinic) {
      toast({
        title: "No Clinic Setup",
        description: "Please set up your clinic first to run emergency protocols.",
        variant: "destructive",
      });
      return;
    }

    if (!selectedScenario) {
      toast({
        title: "Select Scenario",
        description: "Please select an emergency scenario to demonstrate.",
        variant: "destructive",
      });
      return;
    }

    setIsActive(true);
    setCurrentStep(0);
    setProtocolComplete(false);

    const scenario = emergencyScenarios.find(s => s.id === selectedScenario);
    if (!scenario) return;

    // Execute protocol steps with delays
    for (let i = 0; i < scenario.protocol.length; i++) {
      await new Promise(resolve => setTimeout(resolve, 1500));
      setCurrentStep(i + 1);
    }

    setProtocolComplete(true);
    setIsActive(false);

    toast({
      title: "Emergency Protocol Complete",
      description: `${scenario.title} protocol executed successfully.`,
    });
  };

  const resetDemo = () => {
    setIsActive(false);
    setCurrentStep(0);
    setSelectedScenario("");
    setProtocolComplete(false);
  };

  const selectedEmergency = emergencyScenarios.find(s => s.id === selectedScenario);

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical': return 'bg-red-100 text-red-800 border-red-200';
      case 'high': return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center">
          <Shield className="h-5 w-5 mr-2 text-red-600" />
          Emergency Protocol Simulation
        </CardTitle>
        <p className="text-sm text-gray-600">
          Test how your AI receptionist handles medical emergencies and critical situations
        </p>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Scenario Selection */}
        <div className="space-y-4">
          <h4 className="font-medium">Select Emergency Scenario</h4>
          <div className="grid grid-cols-1 gap-3">
            {emergencyScenarios.map((scenario) => {
              const Icon = scenario.icon;
              return (
                <div
                  key={scenario.id}
                  className={`p-4 border rounded-lg cursor-pointer transition-all ${
                    selectedScenario === scenario.id 
                      ? 'border-blue-500 bg-blue-50' 
                      : 'border-gray-200 hover:border-gray-300'
                  } ${isActive ? 'opacity-50 cursor-not-allowed' : ''}`}
                  onClick={() => !isActive && setSelectedScenario(scenario.id)}
                >
                  <div className="flex items-start justify-between">
                    <div className="flex items-start space-x-3">
                      <Icon className={`h-5 w-5 mt-1 text-${scenario.color}-600`} />
                      <div className="flex-1">
                        <h5 className="font-medium">{scenario.title}</h5>
                        <p className="text-sm text-gray-600 mt-1">{scenario.description}</p>
                      </div>
                    </div>
                    <Badge className={getSeverityColor(scenario.severity)}>
                      {scenario.severity.toUpperCase()}
                    </Badge>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Demo Controls */}
        <div className="flex space-x-3">
          <Button 
            onClick={isActive ? () => {} : (protocolComplete ? resetDemo : startProtocol)}
            disabled={isActive}
            className="flex-1"
            variant={protocolComplete ? "outline" : "default"}
          >
            {isActive ? "Running Protocol..." : protocolComplete ? "Reset Demo" : "Start Emergency Protocol"}
          </Button>
        </div>

        {/* Protocol Execution */}
        {selectedEmergency && currentStep > 0 && (
          <div className="space-y-4">
            {/* Emergency Alert */}
            <Alert className="border-red-200 bg-red-50">
              <AlertTriangle className="h-4 w-4 text-red-600" />
              <AlertDescription className="text-red-800">
                <strong>EMERGENCY PROTOCOL ACTIVATED:</strong> {selectedEmergency.title}
              </AlertDescription>
            </Alert>

            {/* Progress */}
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>Protocol Progress</span>
                <span>{currentStep}/{selectedEmergency.protocol.length} steps</span>
              </div>
              <Progress 
                value={(currentStep / selectedEmergency.protocol.length) * 100} 
                className="h-2"
              />
            </div>

            {/* Protocol Steps */}
            <div className="space-y-3">
              {selectedEmergency.protocol.map((step, index) => (
                <div
                  key={index}
                  className={`flex items-start space-x-3 p-3 rounded-lg transition-all ${
                    index < currentStep 
                      ? 'bg-green-50 border border-green-200' 
                      : index === currentStep - 1 && isActive
                      ? 'bg-blue-50 border border-blue-200 animate-pulse'
                      : 'bg-gray-50 border border-gray-200 opacity-50'
                  }`}
                >
                  {index < currentStep ? (
                    <CheckCircle className="h-5 w-5 text-green-600 mt-0.5 flex-shrink-0" />
                  ) : index === currentStep - 1 && isActive ? (
                    <Clock className="h-5 w-5 text-blue-600 mt-0.5 flex-shrink-0 animate-spin" />
                  ) : (
                    <div className="h-5 w-5 rounded-full border-2 border-gray-300 mt-0.5 flex-shrink-0" />
                  )}
                  <div className="flex-1">
                    <p className={`text-sm ${
                      index < currentStep ? 'text-green-800 font-medium' : 'text-gray-600'
                    }`}>
                      {step}
                    </p>
                    {index < currentStep && (
                      <p className="text-xs text-green-600 mt-1">âœ“ Completed</p>
                    )}
                  </div>
                </div>
              ))}
            </div>

            {/* Completion Summary */}
            {protocolComplete && (
              <div className="bg-green-50 border border-green-200 rounded-lg p-4">
                <div className="flex items-center space-x-2 mb-2">
                  <CheckCircle className="h-5 w-5 text-green-600" />
                  <h4 className="font-semibold text-green-800">Emergency Protocol Completed</h4>
                </div>
                <div className="text-sm text-green-700 space-y-1">
                  <p>â€¢ All emergency steps executed successfully</p>
                  <p>â€¢ Incident logged with timestamp</p>
                  <p>â€¢ Notifications sent to relevant personnel</p>
                  <p>â€¢ Follow-up procedures scheduled</p>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Protocol Features */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6">
          <div className="bg-red-50 p-4 rounded-lg">
            <h4 className="font-medium text-red-900 mb-2 flex items-center">
              <AlertTriangle className="h-4 w-4 mr-2" />
              Immediate Response
            </h4>
            <p className="text-sm text-red-700">
              AI instantly recognizes emergency keywords and activates appropriate protocols.
            </p>
          </div>

          <div className="bg-blue-50 p-4 rounded-lg">
            <h4 className="font-medium text-blue-900 mb-2 flex items-center">
              <Phone className="h-4 w-4 mr-2" />
              Emergency Services
            </h4>
            <p className="text-sm text-blue-700">
              Direct connection to 999, NHS 111, and crisis helplines with proper escalation.
            </p>
          </div>

          <div className="bg-purple-50 p-4 rounded-lg">
            <h4 className="font-medium text-purple-900 mb-2 flex items-center">
              <Shield className="h-4 w-4 mr-2" />
              Documentation
            </h4>
            <p className="text-sm text-purple-700">
              Complete incident logging with medical details and timeline for legal compliance.
            </p>
          </div>

          <div className="bg-green-50 p-4 rounded-lg">
            <h4 className="font-medium text-green-900 mb-2 flex items-center">
              <CheckCircle className="h-4 w-4 mr-2" />
              Follow-up Care
            </h4>
            <p className="text-sm text-green-700">
              Automatic scheduling of follow-up appointments and care coordination.
            </p>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

