import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { 
  Settings, 
  CheckCircle, 
  XCircle, 
  Clock, 
  Database,
  Calendar,
  MessageSquare,
  Phone,
  FileText,
  Zap,
  AlertTriangle
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface IntegrationTestingDemoProps {
  clinic?: any;
}

interface IntegrationTest {
  id: string;
  name: string;
  description: string;
  category: string;
  icon: any;
  steps: string[];
  status: 'pending' | 'running' | 'success' | 'error';
  duration: number;
  required: boolean;
}

export default function IntegrationTestingDemo({ clinic }: IntegrationTestingDemoProps) {
  const { toast } = useToast();
  const [isRunning, setIsRunning] = useState(false);
  const [currentTest, setCurrentTest] = useState<string>("");
  const [testResults, setTestResults] = useState<Record<string, 'success' | 'error' | 'pending'>>({});
  const [progress, setProgress] = useState(0);

  const integrationTests: IntegrationTest[] = [
    {
      id: "database-connection",
      name: "Database Connection",
      description: "Test database connectivity and query performance",
      category: "Core Systems",
      icon: Database,
      steps: [
        "Establish database connection",
        "Test read/write operations",
        "Verify data integrity",
        "Check query performance"
      ],
      status: 'pending',
      duration: 2000,
      required: true
    },
    {
      id: "calendar-sync",
      name: "Calendar Integration", 
      description: "Test appointment synchronization with calendar systems",
      category: "Scheduling",
      icon: Calendar,
      steps: [
        "Connect to calendar API",
        "Sync existing appointments",
        "Test new appointment creation",
        "Verify conflict detection"
      ],
      status: 'pending',
      duration: 3000,
      required: true
    },
    {
      id: "sms-notifications",
      name: "SMS Service",
      description: "Test SMS notification delivery and status tracking",
      category: "Communications",
      icon: MessageSquare,
      steps: [
        "Connect to Twilio API",
        "Send test SMS message",
        "Verify delivery status",
        "Test message templates"
      ],
      status: 'pending',
      duration: 2500,
      required: false
    },
    {
      id: "voice-system",
      name: "Voice Recognition",
      description: "Test AI voice processing and speech synthesis",
      category: "AI Services",
      icon: Phone,
      steps: [
        "Initialize voice engine",
        "Test speech recognition",
        "Verify text-to-speech",
        "Check accent handling"
      ],
      status: 'pending',
      duration: 4000,
      required: true
    },
    {
      id: "patient-records",
      name: "Patient Records",
      description: "Test patient data handling and HIPAA compliance",
      category: "Data Management",
      icon: FileText,
      steps: [
        "Validate encryption",
        "Test access controls",
        "Verify audit logging",
        "Check data retention"
      ],
      status: 'pending',
      duration: 3500,
      required: true
    },
    {
      id: "external-apis",
      name: "External APIs",
      description: "Test third-party service integrations",
      category: "External Services", 
      icon: Zap,
      steps: [
        "Test NHS API connection",
        "Verify insurance lookup", 
        "Check medication database",
        "Test emergency services"
      ],
      status: 'pending',
      duration: 5000,
      required: false
    }
  ];

  const runAllTests = async () => {
    if (!clinic) {
      toast({
        title: "No Clinic Setup",
        description: "Please set up your clinic first to run integration tests.",
        variant: "destructive",
      });
      return;
    }

    setIsRunning(true);
    setTestResults({});
    setProgress(0);

    for (let i = 0; i < integrationTests.length; i++) {
      const test = integrationTests[i];
      setCurrentTest(test.id);

      // Simulate test execution
      const success = Math.random() > 0.1; // 90% success rate

      await new Promise(resolve => setTimeout(resolve, test.duration));

      setTestResults(prev => ({
        ...prev,
        [test.id]: success ? 'success' : 'error'
      }));

      setProgress(((i + 1) / integrationTests.length) * 100);
    }

    setCurrentTest("");
    setIsRunning(false);

    const successCount = Object.values(testResults).filter(r => r === 'success').length;
    const totalTests = integrationTests.length;

    toast({
      title: "Integration Testing Complete",
      description: `${successCount}/${totalTests} tests passed successfully.`,
      variant: successCount === totalTests ? "default" : "destructive",
    });
  };

  const runSingleTest = async (testId: string) => {
    if (!clinic) {
      toast({
        title: "No Clinic Setup", 
        description: "Please set up your clinic first to run tests.",
        variant: "destructive",
      });
      return;
    }

    const test = integrationTests.find(t => t.id === testId);
    if (!test) return;

    setCurrentTest(testId);

    // Simulate test execution
    const success = Math.random() > 0.1;

    await new Promise(resolve => setTimeout(resolve, test.duration));

    setTestResults(prev => ({
      ...prev,
      [testId]: success ? 'success' : 'error'
    }));

    setCurrentTest("");

    toast({
      title: `${test.name} Test Complete`,
      description: success ? "Test passed successfully." : "Test failed. Check configuration.",
      variant: success ? "default" : "destructive",
    });
  };

  const resetTests = () => {
    setTestResults({});
    setCurrentTest("");
    setProgress(0);
  };

  const getStatusIcon = (testId: string) => {
    if (currentTest === testId) {
      return <Clock className="h-4 w-4 text-blue-600 animate-spin" />;
    }

    const result = testResults[testId];
    if (result === 'success') {
      return <CheckCircle className="h-4 w-4 text-green-600" />;
    } else if (result === 'error') {
      return <XCircle className="h-4 w-4 text-red-600" />;
    }

    return <div className="h-4 w-4 rounded-full border-2 border-gray-300" />;
  };

  const getStatusBadge = (testId: string) => {
    if (currentTest === testId) {
      return <Badge className="bg-blue-100 text-blue-800">Running</Badge>;
    }

    const result = testResults[testId];
    if (result === 'success') {
      return <Badge className="bg-green-100 text-green-800">Passed</Badge>;
    } else if (result === 'error') {
      return <Badge className="bg-red-100 text-red-800">Failed</Badge>;
    }

    return <Badge variant="outline">Pending</Badge>;
  };

  const categorizedTests = integrationTests.reduce((acc, test) => {
    if (!acc[test.category]) {
      acc[test.category] = [];
    }
    acc[test.category].push(test);
    return acc;
  }, {} as Record<string, IntegrationTest[]>);

  const totalTests = integrationTests.length;
  const completedTests = Object.keys(testResults).length;
  const passedTests = Object.values(testResults).filter(r => r === 'success').length;
  const failedTests = Object.values(testResults).filter(r => r === 'error').length;

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center">
          <Settings className="h-5 w-5 mr-2" />
          Integration Testing Suite
        </CardTitle>
        <p className="text-sm text-gray-600">
          Test all system integrations and validate clinic setup
        </p>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Test Summary */}
        {completedTests > 0 && (
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="bg-blue-50 p-3 rounded-lg text-center">
              <div className="text-2xl font-bold text-blue-900">{totalTests}</div>
              <div className="text-sm text-blue-700">Total Tests</div>
            </div>
            <div className="bg-green-50 p-3 rounded-lg text-center">
              <div className="text-2xl font-bold text-green-900">{passedTests}</div>
              <div className="text-sm text-green-700">Passed</div>
            </div>
            <div className="bg-red-50 p-3 rounded-lg text-center">
              <div className="text-2xl font-bold text-red-900">{failedTests}</div>
              <div className="text-sm text-red-700">Failed</div>
            </div>
            <div className="bg-gray-50 p-3 rounded-lg text-center">
              <div className="text-2xl font-bold text-gray-900">{totalTests - completedTests}</div>
              <div className="text-sm text-gray-700">Pending</div>
            </div>
          </div>
        )}

        {/* Progress Bar */}
        {isRunning && (
          <div className="space-y-2">
            <div className="flex justify-between text-sm">
              <span>Overall Progress</span>
              <span>{Math.round(progress)}%</span>
            </div>
            <Progress value={progress} className="h-2" />
          </div>
        )}

        {/* Control Buttons */}
        <div className="flex space-x-3">
          <Button 
            onClick={runAllTests}
            disabled={isRunning}
            className="flex-1"
          >
            {isRunning ? "Running Tests..." : "Run All Tests"}
          </Button>

          <Button 
            variant="outline" 
            onClick={resetTests}
            disabled={isRunning}
          >
            Reset
          </Button>
        </div>

        {/* Test Categories */}
        <div className="space-y-6">
          {Object.entries(categorizedTests).map(([category, tests]) => (
            <div key={category}>
              <h4 className="font-medium mb-3">{category}</h4>
              <div className="space-y-3">
                {tests.map((test) => {
                  const Icon = test.icon;
                  return (
                    <div
                      key={test.id}
                      className="flex items-center justify-between p-4 border rounded-lg hover:bg-gray-50"
                    >
                      <div className="flex items-center space-x-3">
                        {getStatusIcon(test.id)}
                        <Icon className="h-5 w-5 text-gray-600" />
                        <div className="flex-1">
                          <div className="flex items-center space-x-2">
                            <h5 className="font-medium">{test.name}</h5>
                            {test.required && (
                              <Badge variant="outline" className="text-xs">Required</Badge>
                            )}
                          </div>
                          <p className="text-sm text-gray-600">{test.description}</p>
                        </div>
                      </div>

                      <div className="flex items-center space-x-3">
                        {getStatusBadge(test.id)}
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => runSingleTest(test.id)}
                          disabled={isRunning}
                        >
                          Test
                        </Button>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          ))}
        </div>

        {/* Failed Tests Alert */}
        {failedTests > 0 && !isRunning && (
          <Alert className="border-red-200 bg-red-50">
            <AlertTriangle className="h-4 w-4 text-red-600" />
            <AlertDescription className="text-red-800">
              <strong>{failedTests} test(s) failed.</strong> Please check your configuration and API credentials.
              Failed tests may impact clinic functionality.
            </AlertDescription>
          </Alert>
        )}

        {/* Success Message */}
        {completedTests === totalTests && failedTests === 0 && !isRunning && (
          <Alert className="border-green-200 bg-green-50">
            <CheckCircle className="h-4 w-4 text-green-600" />
            <AlertDescription className="text-green-800">
              <strong>All tests passed!</strong> Your clinic integration is ready for production use.
            </AlertDescription>
          </Alert>
        )}

        {/* Test Details */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="bg-blue-50 p-4 rounded-lg">
            <h4 className="font-medium text-blue-900 mb-2">Comprehensive Testing</h4>
            <p className="text-sm text-blue-700">
              Tests cover all critical systems including database, APIs, voice services, and security.
            </p>
          </div>

          <div className="bg-purple-50 p-4 rounded-lg">
            <h4 className="font-medium text-purple-900 mb-2">Real-time Monitoring</h4>
            <p className="text-sm text-purple-700">
              Continuous monitoring ensures all integrations remain healthy and functional.
            </p>
          </div>

          <div className="bg-green-50 p-4 rounded-lg">
            <h4 className="font-medium text-green-900 mb-2">Automated Diagnostics</h4>
            <p className="text-sm text-green-700">
              Automatic detection and reporting of integration issues with detailed error logs.
            </p>
          </div>

          <div className="bg-orange-50 p-4 rounded-lg">
            <h4 className="font-medium text-orange-900 mb-2">Performance Validation</h4>
            <p className="text-sm text-orange-700">
              Tests verify system performance under load and validate response times.
            </p>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

