import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { 
  Phone, 
  MessageCircle, 
  Calendar, 
  Clock, 
  User, 
  CheckCircle, 
  X,
  Mic,
  Volume2,
  PlayCircle,
  StopCircle
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface InteractiveBookingDemoProps {
  clinic?: any;
}

interface ConversationStep {
  id: number;
  speaker: 'ai' | 'patient';
  message: string;
  timestamp: Date;
  options?: string[];
  nextStep?: number;
  isTyping?: boolean;
}

export default function InteractiveBookingDemo({ clinic }: InteractiveBookingDemoProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isActive, setIsActive] = useState(false);
  const [currentStep, setCurrentStep] = useState(0);
  const [conversation, setConversation] = useState<ConversationStep[]>([]);
  const [patientResponse, setPatientResponse] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [callData, setCallData] = useState({
    patientName: "",
    phoneNumber: "+44 7700 900123",
    appointmentType: "",
    preferredDate: "",
    preferredTime: "",
    duration: 0,
  });

  const createAppointmentMutation = useMutation({
    mutationFn: (data: any) => apiRequest('/api/appointments', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/appointments'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
      toast({
        title: "Appointment Booked Successfully",
        description: "The appointment has been added to your calendar.",
      });
    },
  });

  const createCallLogMutation = useMutation({
    mutationFn: (data: any) => apiRequest('/api/call-logs', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/call-logs'] });
    },
  });

  const conversationFlow: Record<number, any> = {
    0: {
      id: 0,
      speaker: 'ai' as const,
      message: "Good afternoon, thank you for calling [Clinic Name]. This is your AI receptionist. How may I help you today?",
      timestamp: new Date(),
      options: [
        "I'd like to book an appointment",
        "I need to reschedule my appointment", 
        "I have a question about my prescription",
        "This is an emergency"
      ],
      nextStep: {
        "I'd like to book an appointment": 1,
        "I need to reschedule my appointment": 10,
        "I have a question about my prescription": 11, 
        "This is an emergency": 12
      }
    },
    1: {
      id: 1,
      speaker: 'ai' as const,
      message: "I'd be happy to help you book an appointment. May I have your full name please?",
      timestamp: new Date(),
      inputField: 'patientName',
      nextStep: 2
    },
    2: {
      id: 2,
      speaker: 'ai' as const,
      message: "Thank you, [patientName]. What type of appointment would you like to schedule?",
      timestamp: new Date(),
      options: [
        "General consultation",
        "Follow-up appointment",
        "Annual check-up",
        "Urgent care",
        "Specialist referral"
      ],
      nextStep: {
        "General consultation": 3,
        "Follow-up appointment": 3,
        "Annual check-up": 3,
        "Urgent care": 3,
        "Specialist referral": 3
      }
    },
    3: {
      id: 3,
      speaker: 'ai' as const,
      message: "Perfect. Let me check our availability. What would be your preferred date and time?",
      timestamp: new Date(),
      options: [
        "Tomorrow morning",
        "This week afternoon", 
        "Next week anytime",
        "As soon as possible",
        "Let me specify exact date"
      ],
      nextStep: 4
    },
    4: {
      id: 4,
      speaker: 'ai' as const,
      message: "Great! I have availability on Tuesday at 2:30 PM with Dr. Smith. Would that work for you?",
      timestamp: new Date(),
      options: [
        "Yes, that's perfect",
        "Can we do earlier?",
        "Do you have Wednesday available?",
        "I prefer afternoon slots"
      ],
      nextStep: {
        "Yes, that's perfect": 5,
        "Can we do earlier?": 6,
        "Do you have Wednesday available?": 7,
        "I prefer afternoon slots": 8
      }
    },
    5: {
      id: 5,
      speaker: 'ai' as const,
      message: "Excellent! Your appointment is confirmed for Tuesday at 2:30 PM with Dr. Smith. You'll receive a confirmation SMS shortly. Is there anything else I can help you with today?",
      timestamp: new Date(),
      options: [
        "No, that's all. Thank you!",
        "Can you remind me about parking?",
        "What should I bring to my appointment?"
      ],
      nextStep: 9
    },
    6: {
      id: 6,
      speaker: 'ai' as const,
      message: "I have an earlier slot at 10:00 AM on Tuesday. Would that work better for you?",
      timestamp: new Date(),
      options: [
        "Yes, 10 AM is perfect",
        "That's too early, keep the 2:30 PM",
        "What about 11 AM or noon?"
      ],
      nextStep: 5
    },
    7: {
      id: 7,
      speaker: 'ai' as const,
      message: "Yes, I have Wednesday at 1:00 PM or 3:30 PM available. Which would you prefer?",
      timestamp: new Date(),
      options: [
        "1:00 PM works great",
        "3:30 PM is better",
        "Actually, Tuesday is fine"
      ],
      nextStep: 5
    },
    8: {
      id: 8,
      speaker: 'ai' as const,
      message: "I have several afternoon options: Tuesday 2:30 PM, Wednesday 3:30 PM, or Thursday 1:00 PM. Which would you prefer?",
      timestamp: new Date(),
      options: [
        "Tuesday 2:30 PM",
        "Wednesday 3:30 PM", 
        "Thursday 1:00 PM"
      ],
      nextStep: 5
    },
    9: {
      id: 9,
      speaker: 'ai' as const,
      message: "Perfect! Have a wonderful day and we'll see you on Tuesday. Goodbye!",
      timestamp: new Date(),
      isComplete: true
    },
    10: {
      id: 10,
      speaker: 'ai' as const,
      message: "I can help you reschedule. May I have your name and current appointment details?",
      timestamp: new Date(),
      inputField: 'rescheduleInfo',
      nextStep: 9
    },
    11: {
      id: 11,
      speaker: 'ai' as const,
      message: "For prescription inquiries, I'll connect you with our pharmacy team. Please hold while I transfer your call.",
      timestamp: new Date(),
      isComplete: true
    },
    12: {
      id: 12,
      speaker: 'ai' as const,
      message: "For medical emergencies, please hang up and dial 999 immediately. If this is urgent but not life-threatening, I can schedule you for our urgent care slots.",
      timestamp: new Date(),
      options: [
        "Yes, schedule urgent care",
        "I'll call 999 instead"
      ],
      nextStep: 1
    }
  };

  const startSimulation = () => {
    if (!clinic) {
      toast({
        title: "No Clinic Setup",
        description: "Please set up your clinic first to run simulations.",
        variant: "destructive",
      });
      return;
    }

    setIsActive(true);
    setCurrentStep(0);
    setConversation([]);
    setCallData({
      patientName: "",
      phoneNumber: "+44 7700 900123",
      appointmentType: "",
      preferredDate: "",
      preferredTime: "",
      duration: 0,
    });

    // Add initial AI greeting
    addAIMessage(conversationFlow[0]);
  };

  const addAIMessage = (step: any) => {
    setIsTyping(true);

    setTimeout(() => {
      const aiMessage: ConversationStep = {
        id: step.id,
        speaker: 'ai',
        message: step.message.replace('[patientName]', callData.patientName || '[Patient Name]'),
        timestamp: new Date(),
        options: step.options,
        nextStep: step.nextStep,
      };

      setConversation(prev => [...prev, aiMessage]);
      setIsTyping(false);
    }, 1000 + Math.random() * 1000); // Simulate typing delay
  };

  const handlePatientResponse = (response: string) => {
    // Add patient message
    const patientMessage: ConversationStep = {
      id: conversation.length + 1,
      speaker: 'patient',
      message: response,
      timestamp: new Date(),
    };

    setConversation(prev => [...prev, patientMessage]);

    // Update call data based on response
    const currentFlowStep = conversationFlow[currentStep];

    if (currentFlowStep.inputField === 'patientName') {
      setCallData(prev => ({ ...prev, patientName: response }));
    }

    // Determine next step
    let nextStepId: number | undefined;
    if (typeof currentFlowStep.nextStep === 'object') {
      nextStepId = currentFlowStep.nextStep[response] || Object.values(currentFlowStep.nextStep)[0] as number;
    } else {
      nextStepId = currentFlowStep.nextStep as number;
    }

    if (nextStepId && conversationFlow[nextStepId]) {
      setCurrentStep(nextStepId);

      // Add AI response after a delay
      setTimeout(() => {
        addAIMessage(conversationFlow[nextStepId as number]);

        // Check if conversation is complete
        if (conversationFlow[nextStepId as number]?.isComplete) {
          handleBookingComplete();
        }
      }, 500);
    }
  };

  const handleBookingComplete = async () => {
    if (currentStep >= 5 && callData.patientName) {
      // Create appointment
      const appointmentData = {
        clinicId: clinic.id,
        patientName: callData.patientName,
        phoneNumber: callData.phoneNumber,
        appointmentType: "General consultation",
        appointmentDate: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString(), // Tomorrow
        status: 'scheduled',
        source: 'ai_receptionist',
        duration: 30,
      };

      // Create call log
      const callLogData = {
        clinicId: clinic.id,
        phoneNumber: callData.phoneNumber,
        callerName: callData.patientName,
        duration: Math.floor((Date.now() - conversation[0]?.timestamp.getTime()) / 1000),
        summary: `Patient ${callData.patientName} called to book an appointment. Successfully scheduled for Tuesday at 2:30 PM.`,
        transcript: conversation.map(msg => `[${msg.speaker.toUpperCase()}]: ${msg.message}`).join('\n'),
        appointmentBooked: true,
        sentiment: 0.9,
        status: 'completed',
        aiResponse: true,
      };

      try {
        await createAppointmentMutation.mutateAsync(appointmentData);
        await createCallLogMutation.mutateAsync(callLogData);
      } catch (error) {
        console.error('Error saving booking:', error);
      }
    }

    setTimeout(() => {
      setIsActive(false);
    }, 2000);
  };

  const resetSimulation = () => {
    setIsActive(false);
    setCurrentStep(0);
    setConversation([]);
    setPatientResponse("");
    setCallData({
      patientName: "",
      phoneNumber: "+44 7700 900123",
      appointmentType: "",
      preferredDate: "",
      preferredTime: "",
      duration: 0,
    });
  };

  const handleCustomResponse = () => {
    if (patientResponse.trim()) {
      handlePatientResponse(patientResponse);
      setPatientResponse("");
    }
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <span className="flex items-center">
              <MessageCircle className="h-5 w-5 mr-2" />
              Interactive AI Receptionist Demo
            </span>
            <div className="flex items-center space-x-2">
              {isActive ? (
                <Badge variant="destructive" className="animate-pulse">
                  <StopCircle className="h-3 w-3 mr-1" />
                  Live Call
                </Badge>
              ) : (
                <Badge variant="outline">
                  <PlayCircle className="h-3 w-3 mr-1" />
                  Ready
                </Badge>
              )}
            </div>
          </CardTitle>
          <p className="text-sm text-gray-600">
            Experience a realistic conversation with your AI receptionist
          </p>
        </CardHeader>
        <CardContent>
          {!isActive && conversation.length === 0 ? (
            <div className="text-center space-y-6 py-8">
              <div className="flex justify-center">
                <div className="relative">
                  <Phone className="h-16 w-16 text-blue-600" />
                  <div className="absolute -top-1 -right-1 h-4 w-4 bg-green-500 rounded-full"></div>
                </div>
              </div>
              <div>
                <h3 className="text-lg font-semibold mb-2">Start Interactive Demo</h3>
                <p className="text-gray-600 mb-4">
                  This simulation lets you have a real conversation with your AI receptionist.
                  Choose from suggested responses or type your own.
                </p>
                <Button 
                  onClick={startSimulation} 
                  disabled={!clinic}
                  className="px-8"
                >
                  {!clinic ? "Setup Clinic First" : "Start Phone Simulation"}
                </Button>
              </div>
            </div>
          ) : (
            <div className="space-y-4">
              {/* Call Header */}
              <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                <div className="flex items-center space-x-3">
                  <Avatar>
                    <AvatarFallback>
                      <Phone className="h-4 w-4" />
                    </AvatarFallback>
                  </Avatar>
                  <div>
                    <p className="font-medium">
                      {callData.patientName || "Unknown Caller"}
                    </p>
                    <p className="text-sm text-gray-600">{callData.phoneNumber}</p>
                  </div>
                </div>
                <div className="text-right">
                  <p className="text-sm text-gray-600">Duration</p>
                  <p className="font-medium">
                    {Math.floor((Date.now() - (conversation[0]?.timestamp.getTime() || Date.now())) / 1000)}s
                  </p>
                </div>
              </div>

              {/* Conversation */}
              <div className="max-h-96 overflow-y-auto space-y-3 p-4 bg-white border rounded-lg">
                {conversation.map((msg) => (
                  <div
                    key={msg.id}
                    className={`flex ${msg.speaker === 'ai' ? 'justify-start' : 'justify-end'}`}
                  >
                    <div
                      className={`max-w-xs px-4 py-2 rounded-lg ${
                        msg.speaker === 'ai'
                          ? 'bg-blue-100 text-blue-900'
                          : 'bg-green-100 text-green-900'
                      }`}
                    >
                      <div className="flex items-center space-x-2 mb-1">
                        {msg.speaker === 'ai' ? (
                          <MessageCircle className="h-3 w-3" />
                        ) : (
                          <User className="h-3 w-3" />
                        )}
                        <span className="text-xs font-medium">
                          {msg.speaker === 'ai' ? 'AI Receptionist' : 'Patient'}
                        </span>
                      </div>
                      <p className="text-sm">{msg.message}</p>
                      <p className="text-xs opacity-70 mt-1">
                        {msg.timestamp.toLocaleTimeString()}
                      </p>
                    </div>
                  </div>
                ))}

                {isTyping && (
                  <div className="flex justify-start">
                    <div className="bg-blue-100 text-blue-900 px-4 py-2 rounded-lg">
                      <div className="flex items-center space-x-2">
                        <MessageCircle className="h-3 w-3" />
                        <span className="text-xs font-medium">AI Receptionist</span>
                      </div>
                      <div className="flex space-x-1 mt-2">
                        <div className="w-2 h-2 bg-blue-600 rounded-full animate-bounce"></div>
                        <div className="w-2 h-2 bg-blue-600 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                        <div className="w-2 h-2 bg-blue-600 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                      </div>
                    </div>
                  </div>
                )}
              </div>

              {/* Response Options */}
              {isActive && !isTyping && (
                <div className="space-y-3">
                  {conversation.length > 0 && conversation[conversation.length - 1].speaker === 'ai' && (
                    <div>
                      <p className="text-sm font-medium mb-2">Choose your response:</p>
                      <div className="grid gap-2">
                        {conversation[conversation.length - 1].options?.map((option, index) => (
                          <Button
                            key={index}
                            variant="outline"
                            onClick={() => handlePatientResponse(option)}
                            className="text-left justify-start h-auto py-2 px-3"
                          >
                            {option}
                          </Button>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Custom Response */}
                  <div className="flex space-x-2 pt-3 border-t">
                    <Input
                      placeholder="Or type your own response..."
                      value={patientResponse}
                      onChange={(e) => setPatientResponse(e.target.value)}
                      onKeyPress={(e) => e.key === 'Enter' && handleCustomResponse()}
                    />
                    <Button onClick={handleCustomResponse}>Send</Button>
                  </div>
                </div>
              )}

              {/* Completion Message */}
              {!isActive && conversation.length > 0 && (
                <div className="text-center p-4 bg-green-50 rounded-lg">
                  <CheckCircle className="h-8 w-8 text-green-600 mx-auto mb-2" />
                  <h4 className="font-semibold text-green-800 mb-2">Call Completed Successfully</h4>
                  <p className="text-sm text-green-700 mb-4">
                    The appointment has been booked and logged in your system.
                  </p>
                  <Button onClick={resetSimulation} variant="outline">
                    Start New Simulation
                  </Button>
                </div>
              )}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

