import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { useToast } from '@/hooks/use-toast';
import { 
  LifeBuoy, 
  Plus, 
  MessageCircle, 
  CheckSquare, 
  TrendingUp, 
  AlertCircle,
  Clock,
  Users,
  Target,
  Heart,
  Activity,
  CheckCircle,
  Star,
  Send,
  Calendar,
  BookOpen
} from 'lucide-react';
import { format } from 'date-fns';

export default function CustomerSuccess() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Support Tickets
  const { data: ticketsData, isLoading: ticketsLoading } = useQuery({
    queryKey: ['/api/support/tickets'],
    staleTime: 5 * 60 * 1000,
  });

  // Onboarding Checklist
  const { data: checklistData, isLoading: checklistLoading } = useQuery({
    queryKey: ['/api/onboarding/checklist'],
    staleTime: 10 * 60 * 1000,
  });

  // Health Score
  const { data: healthScoreData, isLoading: healthLoading } = useQuery({
    queryKey: ['/api/customer-success/health-score'],
    staleTime: 15 * 60 * 1000,
  });

  // System Status
  const { data: statusData } = useQuery({
    queryKey: ['/api/status'],
    staleTime: 2 * 60 * 1000,
  });

  // Create Support Ticket mutation
  const createTicketMutation = useMutation({
    mutationFn: async (data: { title: string; description: string; priority: string; category: string }) => {
      return await apiRequest('/api/support/tickets', {
        method: 'POST',
        body: data,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/support/tickets'] });
      toast({
        title: 'Ticket Created',
        description: 'Your support ticket has been created successfully.',
      });
      setShowTicketDialog(false);
    },
    onError: () => {
      toast({
        title: 'Error',
        description: 'Failed to create support ticket. Please try again.',
        variant: 'destructive',
      });
    },
  });

  // Update Onboarding Progress mutation
  const updateProgressMutation = useMutation({
    mutationFn: async (data: { stepKey: string; completed: boolean }) => {
      return await apiRequest('/api/onboarding/progress', {
        method: 'POST',
        body: data,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/onboarding/checklist'] });
      toast({
        title: 'Progress Updated',
        description: 'Your onboarding progress has been updated.',
      });
    },
  });

  // State management
  const [showTicketDialog, setShowTicketDialog] = useState(false);
  const [ticketForm, setTicketForm] = useState({
    title: '',
    description: '',
    priority: 'medium',
    category: 'general',
  });

  const handleCreateTicket = () => {
    if (!ticketForm.title || !ticketForm.description) {
      toast({
        title: 'Validation Error',
        description: 'Please provide both a title and description for your ticket.',
        variant: 'destructive',
      });
      return;
    }

    createTicketMutation.mutate(ticketForm);
  };

  const handleStepToggle = (stepKey: string, completed: boolean) => {
    updateProgressMutation.mutate({ stepKey, completed });
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'urgent': return 'destructive';
      case 'high': return 'destructive';
      case 'medium': return 'default';
      case 'low': return 'secondary';
      default: return 'default';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'open': return 'destructive';
      case 'in_progress': return 'default';
      case 'waiting_customer': return 'secondary';
      case 'resolved': return 'outline';
      case 'closed': return 'outline';
      default: return 'default';
    }
  };

  const getHealthScoreColor = (score: number) => {
    if (score >= 80) return 'text-green-600';
    if (score >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  return (
    <div className="container mx-auto py-6 space-y-6" data-testid="customer-success-portal">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold" data-testid="heading-customer-success">Customer Success</h1>
          <p className="text-muted-foreground">
            Get help, track your progress, and monitor system health
          </p>
        </div>
      </div>

      {/* Health Score Overview */}
      {healthScoreData?.healthScore && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <Heart className="w-5 h-5 mr-2" />
              Customer Health Score
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <div className="text-center">
                <div className={`text-3xl font-bold ${getHealthScoreColor(healthScoreData.healthScore.overallScore)}`} data-testid="overall-health-score">
                  {healthScoreData.healthScore.overallScore}
                </div>
                <div className="text-sm text-muted-foreground">Overall Score</div>
                <Progress value={healthScoreData.healthScore.overallScore} className="mt-2" />
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-600" data-testid="usage-score">
                  {healthScoreData.healthScore.usageScore}
                </div>
                <div className="text-sm text-muted-foreground">Usage</div>
                <Progress value={healthScoreData.healthScore.usageScore} className="mt-2" />
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-purple-600" data-testid="engagement-score">
                  {healthScoreData.healthScore.engagementScore}
                </div>
                <div className="text-sm text-muted-foreground">Engagement</div>
                <Progress value={healthScoreData.healthScore.engagementScore} className="mt-2" />
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600" data-testid="support-score">
                  {healthScoreData.healthScore.supportScore}
                </div>
                <div className="text-sm text-muted-foreground">Support</div>
                <Progress value={healthScoreData.healthScore.supportScore} className="mt-2" />
              </div>
            </div>

            {healthScoreData.healthScore.actionItems?.length > 0 && (
              <div className="mt-6">
                <h4 className="font-semibold mb-2">Recommended Actions</h4>
                <ul className="space-y-1">
                  {healthScoreData.healthScore.actionItems.map((item, index) => (
                    <li key={index} className="flex items-center text-sm">
                      <Target className="w-4 h-4 mr-2 text-orange-500" />
                      {item}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </CardContent>
        </Card>
      )}

      <Tabs defaultValue="support" className="w-full">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="support" data-testid="tab-support">Support</TabsTrigger>
          <TabsTrigger value="onboarding" data-testid="tab-onboarding">Onboarding</TabsTrigger>
          <TabsTrigger value="status" data-testid="tab-status">System Status</TabsTrigger>
          <TabsTrigger value="resources" data-testid="tab-resources">Resources</TabsTrigger>
        </TabsList>

        {/* Support Tab */}
        <TabsContent value="support" className="space-y-4">
          <div className="flex justify-between items-center">
            <div>
              <h3 className="text-lg font-semibold">Support Tickets</h3>
              <p className="text-sm text-muted-foreground">
                Get help from our support team
              </p>
            </div>
            <Dialog open={showTicketDialog} onOpenChange={setShowTicketDialog}>
              <DialogTrigger asChild>
                <Button data-testid="button-create-ticket">
                  <Plus className="w-4 h-4 mr-2" />
                  Create Ticket
                </Button>
              </DialogTrigger>
              <DialogContent className="sm:max-w-md">
                <DialogHeader>
                  <DialogTitle>Create Support Ticket</DialogTitle>
                  <DialogDescription>
                    Describe your issue and our team will help you resolve it.
                  </DialogDescription>
                </DialogHeader>
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="ticket-title">Title</Label>
                    <Input
                      id="ticket-title"
                      data-testid="input-ticket-title"
                      placeholder="Brief description of the issue"
                      value={ticketForm.title}
                      onChange={(e) => setTicketForm({ ...ticketForm, title: e.target.value })}
                    />
                  </div>

                  <div>
                    <Label htmlFor="ticket-description">Description</Label>
                    <Textarea
                      id="ticket-description"
                      data-testid="input-ticket-description"
                      placeholder="Provide detailed information about your issue"
                      value={ticketForm.description}
                      onChange={(e) => setTicketForm({ ...ticketForm, description: e.target.value })}
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="ticket-priority">Priority</Label>
                      <Select
                        value={ticketForm.priority}
                        onValueChange={(value) => setTicketForm({ ...ticketForm, priority: value })}
                      >
                        <SelectTrigger data-testid="select-ticket-priority">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="low">Low</SelectItem>
                          <SelectItem value="medium">Medium</SelectItem>
                          <SelectItem value="high">High</SelectItem>
                          <SelectItem value="urgent">Urgent</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>

                    <div>
                      <Label htmlFor="ticket-category">Category</Label>
                      <Select
                        value={ticketForm.category}
                        onValueChange={(value) => setTicketForm({ ...ticketForm, category: value })}
                      >
                        <SelectTrigger data-testid="select-ticket-category">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="general">General</SelectItem>
                          <SelectItem value="technical">Technical</SelectItem>
                          <SelectItem value="billing">Billing</SelectItem>
                          <SelectItem value="onboarding">Onboarding</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                  </div>

                  <div className="flex justify-end space-x-2">
                    <Button 
                      variant="outline" 
                      onClick={() => setShowTicketDialog(false)}
                      data-testid="button-cancel-ticket"
                    >
                      Cancel
                    </Button>
                    <Button 
                      onClick={handleCreateTicket} 
                      disabled={createTicketMutation.isPending}
                      data-testid="button-submit-ticket"
                    >
                      Create Ticket
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          </div>

          {/* Support Tickets List */}
          <div className="space-y-3">
            {ticketsLoading ? (
              <div className="text-center py-8" data-testid="loading-tickets">Loading support tickets...</div>
            ) : ticketsData?.tickets?.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground" data-testid="empty-tickets">
                No support tickets yet. Create your first ticket to get help from our team.
              </div>
            ) : (
              ticketsData?.tickets?.map((ticket: any) => (
                <Card key={ticket.id} data-testid={`ticket-${ticket.id}`}>
                  <CardContent className="p-4">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center space-x-2 mb-2">
                          <LifeBuoy className="w-4 h-4" />
                          <h4 className="font-semibold" data-testid={`ticket-title-${ticket.id}`}>
                            {ticket.title}
                          </h4>
                          <Badge variant={getPriorityColor(ticket.priority)}>
                            {ticket.priority}
                          </Badge>
                          <Badge variant={getStatusColor(ticket.status)}>
                            {ticket.status}
                          </Badge>
                        </div>
                        <p className="text-sm text-muted-foreground mb-2" data-testid={`ticket-description-${ticket.id}`}>
                          {ticket.description.slice(0, 200)}
                          {ticket.description.length > 200 && '...'}
                        </p>
                        <div className="flex items-center text-sm text-muted-foreground space-x-4">
                          <span className="flex items-center">
                            <Calendar className="w-3 h-3 mr-1" />
                            Created {format(new Date(ticket.createdAt), 'MMM d, yyyy')}
                          </span>
                          {ticket.firstResponseAt && (
                            <span className="flex items-center text-green-600">
                              <MessageCircle className="w-3 h-3 mr-1" />
                              Responded
                            </span>
                          )}
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))
            )}
          </div>
        </TabsContent>

        {/* Onboarding Tab */}
        <TabsContent value="onboarding" className="space-y-4">
          <div>
            <h3 className="text-lg font-semibold">Onboarding Checklist</h3>
            <p className="text-sm text-muted-foreground">
              Complete these steps to get the most out of ClinicVoice
            </p>
          </div>

          {checklistLoading ? (
            <div className="text-center py-8" data-testid="loading-checklist">Loading onboarding checklist...</div>
          ) : checklistData?.checklist && (
            <Card>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <CheckSquare className="w-5 h-5 mr-2" />
                    <CardTitle>Getting Started</CardTitle>
                  </div>
                  <div className="text-sm text-muted-foreground">
                    {checklistData.checklist.currentStep} of {checklistData.checklist.totalSteps} completed
                  </div>
                </div>
                <Progress 
                  value={(checklistData.checklist.currentStep / checklistData.checklist.totalSteps) * 100} 
                  className="mt-2"
                />
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {Object.entries(checklistData.checklist.progress).map(([stepKey, step]: [string, any]) => (
                    <div key={stepKey} className="flex items-start space-x-3" data-testid={`step-${stepKey}`}>
                      <Button
                        variant="ghost"
                        size="sm"
                        className="p-0 h-auto"
                        onClick={() => handleStepToggle(stepKey, !step.completed)}
                        disabled={updateProgressMutation.isPending}
                      >
                        {step.completed ? (
                          <CheckCircle className="w-5 h-5 text-green-600" />
                        ) : (
                          <div className="w-5 h-5 border-2 border-muted-foreground rounded-full" />
                        )}
                      </Button>
                      <div className="flex-1">
                        <h4 className={`font-semibold ${step.completed ? 'text-muted-foreground line-through' : ''}`}>
                          {step.name}
                        </h4>
                        <p className="text-sm text-muted-foreground">
                          {step.description}
                        </p>
                        {step.completed && step.completedAt && (
                          <p className="text-xs text-green-600 mt-1">
                            Completed {format(new Date(step.completedAt), 'MMM d, yyyy')}
                          </p>
                        )}
                      </div>
                    </div>
                  ))}
                </div>

                {checklistData.checklist.completedAt && (
                  <Alert className="mt-4">
                    <Star className="h-4 w-4" />
                    <AlertDescription>
                      ðŸŽ‰ Congratulations! You've completed the onboarding process. You're all set to get the most out of ClinicVoice.
                    </AlertDescription>
                  </Alert>
                )}
              </CardContent>
            </Card>
          )}
        </TabsContent>

        {/* System Status Tab */}
        <TabsContent value="status" className="space-y-4">
          <div>
            <h3 className="text-lg font-semibold">System Status</h3>
            <p className="text-sm text-muted-foreground">
              Real-time status of all ClinicVoice services
            </p>
          </div>

          {statusData && (
            <div className="space-y-4">
              {/* Overall Status */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center">
                    <Activity className="w-5 h-5 mr-2" />
                    Overall System Status
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="flex items-center space-x-3">
                    <div className={`w-3 h-3 rounded-full ${
                      statusData.status === 'operational' ? 'bg-green-500' :
                      statusData.status === 'degraded_performance' ? 'bg-yellow-500' :
                      statusData.status === 'partial_outage' ? 'bg-orange-500' :
                      'bg-red-500'
                    }`} />
                    <span className="font-semibold capitalize" data-testid="system-status">
                      {statusData.status.replace('_', ' ')}
                    </span>
                  </div>
                </CardContent>
              </Card>

              {/* Service Status */}
              <Card>
                <CardHeader>
                  <CardTitle>Service Components</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid gap-4 md:grid-cols-2">
                    {Object.entries(statusData.services).map(([service, info]: [string, any]) => (
                      <div key={service} className="flex items-center justify-between p-3 border rounded-lg">
                        <span className="font-medium">{service}</span>
                        <div className="flex items-center space-x-2">
                          <div className={`w-2 h-2 rounded-full ${
                            info.status === 'operational' ? 'bg-green-500' : 
                            info.status === 'degraded' ? 'bg-yellow-500' : 'bg-red-500'
                          }`} />
                          <span className="text-sm capitalize">{info.status}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Current Incidents */}
              {statusData.incidents?.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center">
                      <AlertCircle className="w-5 h-5 mr-2 text-orange-500" />
                      Active Incidents
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      {statusData.incidents.map((incident: any) => (
                        <div key={incident.id} className="border-l-4 border-orange-500 pl-4">
                          <div className="flex items-center space-x-2 mb-1">
                            <h4 className="font-semibold">{incident.title}</h4>
                            <Badge variant={incident.impact === 'critical' ? 'destructive' : 'secondary'}>
                              {incident.impact}
                            </Badge>
                          </div>
                          <p className="text-sm text-muted-foreground mb-2">
                            {incident.description}
                          </p>
                          <div className="flex items-center text-sm text-muted-foreground">
                            <Clock className="w-3 h-3 mr-1" />
                            Started {format(new Date(incident.startedAt), 'MMM d, yyyy h:mm a')}
                          </div>
                        </div>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              )}

              {/* Recent Updates */}
              {statusData.recentUpdates?.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle>Recent Updates</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      {statusData.recentUpdates.slice(0, 5).map((update: any) => (
                        <div key={update.id} className="flex items-start space-x-3">
                          <div className="w-2 h-2 bg-blue-500 rounded-full mt-2" />
                          <div className="flex-1">
                            <p className="text-sm">{update.message}</p>
                            <div className="flex items-center text-xs text-muted-foreground mt-1">
                              <span>{update.incidentTitle}</span>
                              <span className="mx-2">â€¢</span>
                              <span>{format(new Date(update.createdAt), 'MMM d, h:mm a')}</span>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              )}
            </div>
          )}
        </TabsContent>

        {/* Resources Tab */}
        <TabsContent value="resources" className="space-y-4">
          <div>
            <h3 className="text-lg font-semibold">Help & Resources</h3>
            <p className="text-sm text-muted-foreground">
              Find guides, documentation, and additional support
            </p>
          </div>

          <div className="grid gap-4 md:grid-cols-2">
            <Card>
              <CardContent className="p-6">
                <BookOpen className="w-8 h-8 mb-4 text-blue-600" />
                <h3 className="font-semibold mb-2">Documentation</h3>
                <p className="text-sm text-muted-foreground mb-4">
                  Comprehensive guides on using all ClinicVoice features.
                </p>
                <Button variant="outline" size="sm" asChild data-testid="link-documentation">
                  <a href="https://docs.clinicvoice.co.uk" target="_blank" rel="noopener noreferrer">
                    View Docs
                  </a>
                </Button>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <Users className="w-8 h-8 mb-4 text-green-600" />
                <h3 className="font-semibold mb-2">Community</h3>
                <p className="text-sm text-muted-foreground mb-4">
                  Connect with other healthcare professionals using ClinicVoice.
                </p>
                <Button variant="outline" size="sm" asChild data-testid="link-community">
                  <a href="https://community.clinicvoice.co.uk" target="_blank" rel="noopener noreferrer">
                    Join Community
                  </a>
                </Button>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <MessageCircle className="w-8 h-8 mb-4 text-purple-600" />
                <h3 className="font-semibold mb-2">Live Chat</h3>
                <p className="text-sm text-muted-foreground mb-4">
                  Get instant help from our support team during business hours.
                </p>
                <Button variant="outline" size="sm" data-testid="button-live-chat">
                  Start Chat
                </Button>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6">
                <Calendar className="w-8 h-8 mb-4 text-orange-600" />
                <h3 className="font-semibold mb-2">Schedule Demo</h3>
                <p className="text-sm text-muted-foreground mb-4">
                  Book a personalized demo to explore advanced features.
                </p>
                <Button variant="outline" size="sm" asChild data-testid="link-demo">
                  <a href="https://calendly.com/clinicvoice/demo" target="_blank" rel="noopener noreferrer">
                    Book Demo
                  </a>
                </Button>
              </CardContent>
            </Card>
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
}
